import React, { useState } from 'react';
import ClienteConsumoValor from './clienteConsumoValor';
import ClienteMaiorQuantidade from './clienteMaiorQuantidade';
import ClienteMenorConsumo from './clienteMenorConsumo';
import ProdutoServicoConsumo from './produtoServicoConsumo';
import ConsumoGenero from './consumoGenero';

const ListagensTabs = () => {
    const [activeTab, setActiveTab] = useState('1');

    const handleTabChange = (tab: any) => {
        setActiveTab(tab);
    };

    return (
        <div className="container">
            <div className="row">
                {/* Tabs */}
                <div className="col s12">
                    <ul className="tabs">
                        <li className="tab col s3">
                            <a
                                href="#1"
                                onClick={() => handleTabChange('1')}
                                className={activeTab === '1' ? 'active' : ''}
                            >
                                Cliente Consumo Valor
                            </a>
                        </li>
                        <li className="tab col s3">
                            <a
                                href="#2"
                                onClick={() => handleTabChange('2')}
                                className={activeTab === '2' ? 'active' : ''}
                            >
                                Cliente Maior Quantidade
                            </a>
                        </li>
                        <li className="tab col s3">
                            <a
                                href="#3"
                                onClick={() => handleTabChange('3')}
                                className={activeTab === '3' ? 'active' : ''}
                            >
                                Cliente Menor Consumo
                            </a>
                        </li>
                        <li className="tab col s3">
                            <a
                                href="#4"
                                onClick={() => handleTabChange('4')}
                                className={activeTab === '4' ? 'active' : ''}
                            >
                                Produto/Serviço Consumo
                            </a>
                        </li>
                        <li className="tab col s3">
                            <a
                                href="#5"
                                onClick={() => handleTabChange('5')}
                                className={activeTab === '5' ? 'active' : ''}
                            >
                                Consumo por Gênero
                            </a>
                        </li>
                    </ul>
                </div>

                {/* Tab Content */}
                <div className="col s12">
                    {activeTab === '1' && <ClienteConsumoValor />}
                    {activeTab === '2' && <ClienteMaiorQuantidade />}
                    {activeTab === '3' && <ClienteMenorConsumo />}
                    {activeTab === '4' && <ProdutoServicoConsumo />}
                    {activeTab === '5' && <ConsumoGenero />}
                </div>
            </div>
        </div>
    );
};

export default ListagensTabs;
