import React, { Component } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

// Definir o tipo Cliente
type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
};

// Definir o tipo de State
type State = {
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
  clientes: Cliente[];
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

class CadastroCliente extends Component<{}, State> {
  constructor(props: {}) {
    super(props);
    this.state = {
      nome: "",
      cpf: "",
      rg: "",
      dataEmissao: "",
      telefone: "",
      dataCadastro: "",
      genero: "",
      clientes: JSON.parse(localStorage.getItem("clientes") || "[]"),
    };
  }

  handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    this.setState({ [name]: value } as any);
  };

  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const {
      nome,
      cpf,
      rg,
      dataEmissao,
      telefone,
      dataCadastro,
      genero,
      clientes,
    } = this.state;

    const newClient: Cliente = {
      id: Date.now().toString(),
      nome,
      cpf,
      rg,
      dataEmissao,
      telefone,
      dataCadastro,
      genero,
    };

    const updatedClients = [...clientes, newClient];
    localStorage.setItem("clientes", JSON.stringify(updatedClients));
    this.setState({
      nome: "",
      cpf: "",
      rg: "",
      dataEmissao: "",
      telefone: "",
      dataCadastro: "",
      genero: "",
      clientes: updatedClients,
    });
    alert("Cliente cadastrado com sucesso!");
  };

  render() {
    const {
      nome,
      cpf,
      rg,
      dataEmissao,
      telefone,
      dataCadastro,
      genero,
    } = this.state;

    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            <a style={botaoStyle} href="/cadServico">
              Cadastros
            </a>
            <a style={botaoStyle} href="/listaCliente">
              Cliente
            </a>
            <a style={botaoStyle} href="/listaProduto">
              Produto
            </a>
            <a style={botaoStyle} href="/listaPedidos">
              Consumo
            </a>
            <a style={botaoStyle} href="/listaServicos">
              Serviços
            </a>
            <a style={botaoStyle} href="/Listagens">
              Listagens
            </a>
          </div>
        </nav>
        <br />
        <div className="container">
          <h5 className="center-align">Cadastro de Cliente</h5>

          <form onSubmit={this.handleSubmit}>
            <div className="input-field">
              <label>Nome:</label>
              <input
                type="text"
                name="nome"
                value={nome}
                onChange={this.handleChange}
                required
              />
            </div>

            <div className="input-field">
              <label>CPF:</label>
              <input
                type="text"
                name="cpf"
                value={cpf}
                onChange={this.handleChange}
                required
              />
            </div>

            <div className="input-field">
              <label>RG:</label>
              <input
                type="text"
                name="rg"
                value={rg}
                onChange={this.handleChange}
                required
              />
            </div>

            <div className="input-field">
              <label>Data Emissão:</label>
              <input
                type="text"
                name="dataEmissao"
                value={dataEmissao}
                onChange={this.handleChange}
                required
              />
            </div>

            <div className="input-field">
              <label>Telefone:</label>
              <input
                type="text"
                name="telefone"
                value={telefone}
                onChange={this.handleChange}
                required
              />
            </div>

            <div className="input-field">
              <label>Data Cadastro:</label>
              <input
                type="text"
                name="dataCadastro"
                value={dataCadastro}
                onChange={this.handleChange}
                required
              />
            </div>

            <div className="input-field">
              <label>Gênero:</label>
              <select
                name="genero"
                value={genero}
                onChange={this.handleChange}
                required
              >
                <option value="" disabled>
                  Selecione o Gênero
                </option>
                <option value="Masculino">Masculino</option>
                <option value="Feminino">Feminino</option>
                <option value="Outro">Outro</option>
              </select>
            </div>

            <button type="submit" style={actionButtonStyle}>
              Cadastrar Cliente
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default CadastroCliente;
