import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import CSS from "csstype";

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const CadastroConsumo: React.FC = () => {
  const navigate = useNavigate();
  const [produtoA, setProdutoA] = useState({ idPedido: "1", idProduto: "101", nomeProduto: "Produto A", valor: "50.00" });
  const [produtoB, setProdutoB] = useState({ idPedido: "2", idProduto: "102", nomeProduto: "Produto B", valor: "75.00" });
  const [produtoC, setProdutoC] = useState({ idPedido: "3", idProduto: "103", nomeProduto: "Produto C", valor: "100.00" });
  const [consumoSelecionado, setConsumoSelecionado] = useState(produtoA);

  const handleSelectProduto = (produto: any) => {
    setConsumoSelecionado(produto);
  };

  const handleSubmit = () => {
    navigate("/listaPedidos", { state: { novoConsumo: consumoSelecionado } });
  };

  return (
    <div className="container">
      <h5 className="center-align">Cadastro de Consumo</h5>

      <div className="row">
        <div className="col s4">
          <button
            onClick={() => handleSelectProduto(produtoA)}
            className="btn"
            style={botaoStyle}
          >
            Produto A
          </button>
        </div>
        <div className="col s4">
          <button
            onClick={() => handleSelectProduto(produtoB)}
            className="btn"
            style={botaoStyle}
          >
            Produto B
          </button>
        </div>
        <div className="col s4">
          <button
            onClick={() => handleSelectProduto(produtoC)}
            className="btn"
            style={botaoStyle}
          >
            Produto C
          </button>
        </div>
      </div>

      <button type="button" onClick={handleSubmit} className="btn" style={botaoStyle}>
        Registrar
      </button>
    </div>
  );
};

export default CadastroConsumo;
