import React, { useEffect, useState } from "react";

interface Produto {
  nome: string;
  quantidade: number;
  preco: number;
}

const ListaConsumo: React.FC = () => {
  const [produtos, setProdutos] = useState<Produto[]>([]);

  // Carregar produtos do localStorage ao montar o componente
  useEffect(() => {
    const produtosSalvos = JSON.parse(localStorage.getItem("produtos") || "[]");
    setProdutos(produtosSalvos);
  }, []);

  return (
    <div>
      {/* Menu superior fixo */}
      <nav style={{ backgroundColor: "#333", color: "#fff", padding: "15px", textAlign: "center", position: "fixed", width: "100%", top: 0, zIndex: 1 }}>
        <a href="/cadastro" style={{ color: "#fff", marginRight: "20px", textDecoration: "none" }}>Cadastro</a>
        <a href="/lista-consumo" style={{ color: "#fff", textDecoration: "none" }}>Lista de Consumo</a>
      </nav>

      {/* Conteúdo principal com espaçamento para o menu fixo */}
      <div style={{ paddingTop: "70px", padding: "20px" }}>
        <h2>Lista de Consumo</h2>
        {produtos.length > 0 ? (
          <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "20px" }}>
            <thead>
              <tr>
                <th style={{ border: "1px solid #ddd", padding: "10px", backgroundColor: "#f2f2f2" }}>Nome</th>
                <th style={{ border: "1px solid #ddd", padding: "10px", backgroundColor: "#f2f2f2" }}>Quantidade</th>
                <th style={{ border: "1px solid #ddd", padding: "10px", backgroundColor: "#f2f2f2" }}>Preço</th>
              </tr>
            </thead>
            <tbody>
              {produtos.map((produto, index) => (
                <tr key={index}>
                  <td style={{ border: "1px solid #ddd", padding: "10px", textAlign: "center" }}>{produto.nome}</td>
                  <td style={{ border: "1px solid #ddd", padding: "10px", textAlign: "center" }}>{produto.quantidade}</td>
                  <td style={{ border: "1px solid #ddd", padding: "10px", textAlign: "center" }}>{produto.preco.toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhum produto cadastrado.</p>
        )}
      </div>
    </div>
  );
};

export default ListaConsumo;
