import { useEffect, useState } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";
import { Link } from "react-router-dom";
import editar from "../Assets/edit.png";
import excluir from "../Assets/delete.png";

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const ImagemStyle: CSS.Properties = {
  padding: "5px",
  maxHeight: "30px",
  maxWidth: "30px",
};

const ListaServicos = () => {
  const [servicos, setServicos] = useState<any[]>([]);

  useEffect(() => {
    // Carregar os serviços do localStorage
    const servicosCarregados = JSON.parse(localStorage.getItem("servicos") || "[]");
    setServicos(servicosCarregados);
  }, []);

  const handleDelete = (index: number) => {
    const servicosAtualizados = [...servicos];
    servicosAtualizados.splice(index, 1);
    localStorage.setItem("servicos", JSON.stringify(servicosAtualizados));
    setServicos(servicosAtualizados);
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">
            Cadastros
          </a>
          <a style={botaoStyle} href="/listaCliente">
            Cliente
          </a>
          <a style={botaoStyle} href="/listaProduto">
            Produto
          </a>
          <a style={botaoStyle} href="/listaPedidos">
            Consumo
          </a>
          <a style={botaoStyle} href="/listaServicos">
            Serviços
          </a>
          <a style={botaoStyle} href="/Listagens">
            Listagens
          </a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Lista de Serviços</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Serviço</th>
              <th>Preço</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {servicos.map((servico, index) => (
              <tr key={index}>
                <td>{servico.nome}</td>
                <td>{servico.valor}</td>
                <td>
                  <Link to="/formularioEdicaoServico">
                    <img src={editar} style={ImagemStyle} alt="editar" />
                  </Link>
                  <img
                    src={excluir}
                    style={ImagemStyle}
                    alt="excluir"
                    onClick={() => handleDelete(index)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaServicos;
