import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";
import React from "react";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [editingItem, setEditingItem] = useState<any | null>(null);

  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  const handleEdit = (item: any) => {
    setIsEditing(true);
    setEditingItem(item);
  };

  const handleDelete = (id: number) => {
    console.log(`Deletando cliente com ID: ${id}`);
    // Lógica de deletação aqui
  };

  const handleSave = () => {
    console.log("Salvando alterações", editingItem);
    // Lógica de salvar alterações aqui
    setIsEditing(false);
    setEditingItem(null);
  };

  const handleCancel = () => {
    console.log("Cancelando edições");
    setIsEditing(false);
    setEditingItem(null);
  };

  const renderForm = () => {
    if (!editingItem) return null;

    return (
      <div style={{ marginTop: "20px", padding: "20px", backgroundColor: "#f1f1f1" }}>
        <h5>Editar {editingItem.nome || "Cliente"}</h5>
        <form>
          {Object.keys(editingItem).map((key) => (
            <div key={key} style={{ marginBottom: "10px" }}>
              <label>{key}</label>
              <input
                type="text"
                value={editingItem[key]}
                onChange={(e) => setEditingItem({ ...editingItem, [key]: e.target.value })}
              />
            </div>
          ))}
          <div>
            <button type="button" className="btn" onClick={handleSave}>
              Salvar
            </button>
            <button type="button" className="btn red" onClick={handleCancel} style={{ marginLeft: "10px" }}>
              Cancelar
            </button>
          </div>
        </form>
      </div>
    );
  };

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
              <li className="tab col s2"><a href="#4" style={{ color: "black", fontSize: "10px" }}>Prod/Serv Consumo</a></li>
              <li className="tab col s2"><a href="#5" style={{ color: "black", fontSize: "10px" }}>Consumo Gênero</a></li>
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            <form>
              <table className="responsive-table centered">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Nome</th>
                    <th>CPF</th>
                    <th>Telefone</th>
                    <th>Valor Consumido</th>
                    <th>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {[
                    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
                    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
                  ]
                    .map(cliente => (
                      <tr key={cliente.id}>
                        <td>{cliente.id}</td>
                        <td>{cliente.nome}</td>
                        <td>{cliente.cpf}</td>
                        <td>{cliente.telefone}</td>
                        <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                        <td>
                          <button
                            onClick={() => handleEdit(cliente)}
                            className="btn-small blue lighten-2"
                          >
                            Editar
                          </button>
                          <button
                            onClick={() => handleDelete(cliente.id)}
                            className="btn-small red lighten-2"
                            style={{ marginLeft: '5px' }}
                          >
                            Deletar
                          </button>
                        </td>
                      </tr>
                    ))}
                </tbody>
              </table>
              <br />
              <button
                type="button"
                className="btn"
                onClick={() => console.log('Gerando relatório')}
                style={{ backgroundColor: '#000000' }}
              >
                Gerar Relatório
              </button>
            </form>
          </div>
        </div>
      </div>

      {isEditing && renderForm()}
    </div>
  );
};

export default Listagens;
