import React, { useEffect, useState } from 'react';
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";

// Definindo tipos para os dados
type Cliente = {
  id: number;
  nome: string;
  cpf: string;
  telefone: string;
  valorConsumido: number;
  qtdConsumida: number;
};

type ProdutoServico = {
  id: number;
  nome: string;
  valor: number;
};

type ConsumoGenero = {
  id: number;
  clienteNome: string;
  produtoServicoNome: string;
  valorTotal: number;
  genero: 'MASCULINO' | 'FEMININO';
};

const Listagens: React.FC = () => {
  const [clientesValor, setClientesValor] = useState<Cliente[]>([]);
  const [clientesQtd, setClientesQtd] = useState<Cliente[]>([]);
  const [clientesMenorConsumo, setClientesMenorConsumo] = useState<Cliente[]>([]);
  const [produtosServicos, setProdutosServicos] = useState<ProdutoServico[]>([]);
  const [consumoFeminino, setConsumoFeminino] = useState<ConsumoGenero[]>([]);
  const [consumoMasculino, setConsumoMasculino] = useState<ConsumoGenero[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
    fetchData();
  }, []);

  const fetchData = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const responseClientesValor = await fetch('/api/clientes-valor');
      const clientesValorData = await responseClientesValor.json();
      setClientesValor(clientesValorData);

      const responseClientesQtd = await fetch('/api/clientes-qtd');
      const clientesQtdData = await responseClientesQtd.json();
      setClientesQtd(clientesQtdData);

      const responseClientesMenorConsumo = await fetch('/api/clientes-menor-consumo');
      const clientesMenorConsumoData = await responseClientesMenorConsumo.json();
      setClientesMenorConsumo(clientesMenorConsumoData);

      const responseProdutosServicos = await fetch('/api/produtos-servicos');
      const produtosServicosData = await responseProdutosServicos.json();
      setProdutosServicos(produtosServicosData);

      const responseConsumoGenero = await fetch('/api/consumo-genero');
      const consumoGeneroData = await responseConsumoGenero.json();
      setConsumoFeminino(consumoGeneroData.feminino);
      setConsumoMasculino(consumoGeneroData.masculino);

    } catch (error) {
      console.error("Erro ao buscar dados:", error);
      setError("Houve um problema ao carregar os dados. Por favor, tente novamente mais tarde.");
    } finally {
      setIsLoading(false);
    }
  };

  if (isLoading) {
    return (
      <div className="container">
        <div className="progress">
          <div className="indeterminate"></div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="card-panel red lighten-2">
          <span className="white-text">{error}</span>
        </div>
      </div>
    );
  }

  return (
    <div className="row">
      {/* Menu de Navegação à Esquerda */}
      <nav className="col s2 black" style={{ height: "100vh", position: "fixed" }}>
        <div className="nav-wrapper">
          <a className="brand-logo center">World Beauty</a>
          <ul className="nav-mobile">
            <li><a href="/cadServico" aria-label="Ir para Cadastros">Cadastros</a></li>
            <li><a href="/listaCliente" aria-label="Ir para Cliente">Cliente</a></li>
            <li><a href="/listaProduto" aria-label="Ir para Produto">Produto</a></li>
            <li><a href="/listaPedidos" aria-label="Ir para Consumo">Consumo</a></li>
            <li><a href="/listaServicos" aria-label="Ir para Serviços">Serviços</a></li>
            <li><a href="/Listagens" aria-label="Ir para Listagens">Listagens</a></li>
          </ul>
        </div>
      </nav>

      {/* Conteúdo principal */}
      <div className="col s10 offset-s2">
        <div className="container">
          <h5 className="center-align">Listagens por Consumo</h5>

          <div className="row">
            <div className="col s12">
              <ul className="tabs">
                <li className="tab col s2"><a href="#1">Cliente Consumo Valor</a></li>
                <li className="tab col s2"><a href="#2">Cliente Maior Qtd</a></li>
                <li className="tab col s2"><a href="#3">Cliente Menor Consumo</a></li>
                <li className="tab col s2"><a href="#4">Prod/Serv Consumo</a></li>
                <li className="tab col s2"><a href="#5">Consumo Gênero</a></li>
              </ul>
            </div>

            {/* Cliente Consumo Valor */}
            <div id="1" className="col s12">
              <table className="responsive-table centered">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Nome</th>
                    <th>CPF</th>
                    <th>Telefone</th>
                    <th>Valor Consumido</th>
                  </tr>
                </thead>
                <tbody>
                  {clientesValor.map(cliente => (
                    <tr key={cliente.id}>
                      <td>{cliente.id}</td>
                      <td>{cliente.nome}</td>
                      <td>{cliente.cpf}</td>
                      <td>{cliente.telefone}</td>
                      <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Cliente Maior Qtd */}
            <div id="2" className="col s12">
              <table className="responsive-table centered">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Nome</th>
                    <th>CPF</th>
                    <th>Telefone</th>
                    <th>Quantidade Consumida</th>
                  </tr>
                </thead>
                <tbody>
                  {clientesQtd.map(cliente => (
                    <tr key={cliente.id}>
                      <td>{cliente.id}</td>
                      <td>{cliente.nome}</td>
                      <td>{cliente.cpf}</td>
                      <td>{cliente.telefone}</td>
                      <td>{cliente.qtdConsumida}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Cliente Menor Consumo */}
            <div id="3" className="col s12">
              <table className="responsive-table centered">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Nome</th>
                    <th>CPF</th>
                    <th>Telefone</th>
                    <th>Valor Consumido</th>
                  </tr>
                </thead>
                <tbody>
                  {clientesMenorConsumo.map(cliente => (
                    <tr key={cliente.id}>
                      <td>{cliente.id}</td>
                      <td>{cliente.nome}</td>
                      <td>{cliente.cpf}</td>
                      <td>{cliente.telefone}</td>
                      <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Produto/Serviço Consumo */}
            <div id="4" className="col s12">
              <table className="responsive-table centered">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Nome do Produto/Serviço</th>
                    <th>Valor do Produto/Serviço</th>
                  </tr>
                </thead>
                <tbody>
                  {produtosServicos.map(item => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.nome}</td>
                      <td>R$ {item.valor.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            {/* Consumo por Gênero */}
            <div id="5" className="col s12">
              <h5 className="center-align">Feminino</h5>
              <table className="responsive-table centered">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Nome da Cliente</th>
                    <th>Produto/Serviço Consumido</th>
                    <th>Valor do Produto/Serviço</th>
                  </tr>
                </thead>
                <tbody>
                  {consumoFeminino.map(item => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.clienteNome}</td>
                      <td>{item.produtoServicoNome}</td>
                      <td>R$ {item.valorTotal.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <h5 className="center-align">Masculino</h5>
              <table className="responsive-table centered">
                <thead>
                  <tr>
                    <th>Id</th>
                    <th>Nome do Cliente</th>
                    <th>Produto/Serviço Consumido</th>
                    <th>Valor do Produto/Serviço</th>
                  </tr>
                </thead>
                <tbody>
                  {consumoMasculino.map(item => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.clienteNome}</td>
                      <td>{item.produtoServicoNome}</td>
                      <td>R$ {item.valorTotal.toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
