import { Component } from "react";
import CSS from "csstype";

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

export default class CadastroCliente extends Component {
  state = {
    nome: "",
    cpf: "",
    rg: "",
    dataEmissao: "",
    telefone: "",
    dataCadastro: "",
    genero: "",
  };

  handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    this.setState({ [name]: value } as any);
  };

  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const { nome, cpf, rg, dataEmissao, telefone, dataCadastro, genero } = this.state;

    const clienteNovo = {
      id: String(Date.now()),
      nome,
      cpf,
      rg,
      dataEmissao,
      telefone,
      dataCadastro,
      genero,
    };

    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    clientes.push(clienteNovo);
    localStorage.setItem("clientes", JSON.stringify(clientes));

    this.setState({
      nome: "",
      cpf: "",
      rg: "",
      dataEmissao: "",
      telefone: "",
      dataCadastro: "",
      genero: "",
    });

    alert("Cliente cadastrado com sucesso!");
  };

  render() {
    const { nome, cpf, rg, dataEmissao, telefone, dataCadastro, genero } = this.state;

    return (
      <div>
        <nav className="">
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>

            <a style={botaoStyle} href="/cadServico">
              Cadastros
            </a>
            <a style={botaoStyle} href="/listaCliente">
              Cliente
            </a>
            <a style={botaoStyle} href="/listaProduto">
              Produto
            </a>
            <a style={botaoStyle} href="/listaPedidos">
              Consumo
            </a>
            <a style={botaoStyle} href="/listaServicos">
              Serviços
            </a>
            <a style={botaoStyle} href="/Listagens">
              Listagens
            </a>
          </div>
        </nav>

        <div className="container">
          <h5 className="center-align">Cadastro de Cliente</h5>
          <form onSubmit={this.handleSubmit}>
            <div className="input-field">
              <label>Nome:</label>
              <input
                type="text"
                name="nome"
                value={nome}
                onChange={this.handleChange}
                required
              />
            </div>
            <div className="input-field">
              <label>CPF:</label>
              <input
                type="text"
                name="cpf"
                value={cpf}
                onChange={this.handleChange}
                required
              />
            </div>
            <div className="input-field">
              <label>RG:</label>
              <input
                type="text"
                name="rg"
                value={rg}
                onChange={this.handleChange}
                required
              />
            </div>
            <div className="input-field">
              <label>Data Emissão:</label>
              <input
                type="text"
                name="dataEmissao"
                value={dataEmissao}
                onChange={this.handleChange}
                required
              />
            </div>
            <div className="input-field">
              <label>Telefone:</label>
              <input
                type="text"
                name="telefone"
                value={telefone}
                onChange={this.handleChange}
                required
              />
            </div>
            <div className="input-field">
              <label>Data Cadastro:</label>
              <input
                type="text"
                name="dataCadastro"
                value={dataCadastro}
                onChange={this.handleChange}
                required
              />
            </div>

            {/* Título Gênero e opções de radio buttons com padding ajustado */}
            <div className="input-field">
              <label htmlFor="genero" style={{ paddingLeft: "10px" }}>
                Gênero:
              </label>
              <div className="row" style={{ paddingLeft: "20px", marginLeft: "40px" }}>
                <div className="col s12">
                  <label>
                    <input
                      name="genero"
                      type="radio"
                      value="Masculino"
                      checked={genero === "Masculino"}
                      onChange={this.handleChange}
                    />
                    <span>Masculino</span>
                  </label>
                </div>
                <div className="col s12">
                  <label>
                    <input
                      name="genero"
                      type="radio"
                      value="Feminino"
                      checked={genero === "Feminino"}
                      onChange={this.handleChange}
                    />
                    <span>Feminino</span>
                  </label>
                </div>
                <div className="col s12">
                  <label>
                    <input
                      name="genero"
                      type="radio"
                      value="Outro"
                      checked={genero === "Outro"}
                      onChange={this.handleChange}
                    />
                    <span>Outro</span>
                  </label>
                </div>
              </div>
            </div>

            <button type="submit" style={botaoStyle}>
              Registrar Cliente
            </button>
          </form>
        </div>
      </div>
    );
  }
}
