import React, { useState, useEffect } from "react";
import CSS from "csstype";

const backgroundColor: CSS.Properties = { backgroundColor: "#000" };
const botaoStyle: CSS.Properties = { padding: "10px", fontFamily: "arial", backgroundColor: "#000", color: "#fff", border: "none", cursor: "pointer" };
const fontStyle: CSS.Properties = { fontSize: "xx-large", fontFamily: "arial", color: "#fff" };

type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([]);

  useEffect(() => {
    // Aqui você pode carregar os consumos iniciais, por exemplo, de uma API ou do localStorage
  }, []);

  const handleDelete = (id: string) => {
    setConsumos(consumos.filter((consumo) => consumo.idPedido !== id));
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>World Beauty</a>
          {/* Links de navegação */}
        </div>
      </nav>

      <div className="container">
        <h5 className="center-align">Lista de Consumo</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo) => (
                <tr key={consumo.idPedido}>
                  <td>{consumo.idPedido}</td>
                  <td>{consumo.idProduto}</td>
                  <td>{consumo.nomeProduto}</td>
                  <td>{consumo.valor}</td>
                  <td>
                    <button onClick={() => handleDelete(consumo.idPedido)} style={botaoStyle}>Deletar</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5}>Nenhum consumo registrado</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaConsumo;
