import React, { useState, useEffect } from "react";
import CSS from "csstype";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px", // Largura do menu
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh", // Altura para preencher a tela
  display: "flex",
  flexDirection: "column", // Organizar itens na vertical
  justifyContent: "flex-start", // Alinhar ao topo
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px", // Espaço abaixo da logo
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column", // Itens do menu em coluna
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000000",
  color: "#FFFFFF",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([]);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const [editConsumo, setEditConsumo] = useState<Consumo | null>(null);

  useEffect(() => {
    const consumosArmazenados = JSON.parse(localStorage.getItem("consumos") || "[]");
    setConsumos(consumosArmazenados);
  }, []);

  const handleDelete = (index: number) => {
    const updatedConsumos = [...consumos];
    updatedConsumos.splice(index, 1);
    setConsumos(updatedConsumos);
    localStorage.setItem("consumos", JSON.stringify(updatedConsumos));
    alert("Consumo deletado com sucesso!");
  };

  const handleEdit = (index: number) => {
    setEditIndex(index);
    setEditConsumo({ ...consumos[index] });
  };

  const handleSave = () => {
    if (editIndex !== null && editConsumo) {
      const updatedConsumos = [...consumos];
      updatedConsumos[editIndex] = editConsumo;
      setConsumos(updatedConsumos);
      localStorage.setItem("consumos", JSON.stringify(updatedConsumos));
      setEditIndex(null);
      setEditConsumo(null);
    }
  };

  const handleCancel = () => {
    setEditIndex(null);
    setEditConsumo(null);
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (editConsumo) {
      const { name, value } = e.target;
      setEditConsumo({ ...editConsumo, [name]: value });
    }
  };

  return (
    <div style={containerStyle}>
      {/* Menu lateral */}
      <nav style={navStyle}>
        <div style={logoStyle}>World Beauty</div>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Lista de Consumo */}
      <div className="container" style={{ marginLeft: "220px", width: "calc(100% - 220px)" }}>
        <h5 className="center-align">Lista de Consumo</h5>

        {/* Botões de edição */}
        {editIndex === null ? (
          <button onClick={() => setEditIndex(0)} style={actionButtonStyle}>
            Editar Consumo
          </button>
        ) : (
          <>
            <button onClick={handleSave} style={actionButtonStyle}>
              Salvar
            </button>
            <button onClick={handleCancel} style={actionButtonStyle}>
              Cancelar
            </button>
          </>
        )}

        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>
                    {editIndex === index ? (
                      <input
                        type="text"
                        name="idPedido"
                        value={editConsumo?.idPedido || ""}
                        onChange={handleChange}
                      />
                    ) : (
                      consumo.idPedido
                    )}
                  </td>
                  <td>
                    {editIndex === index ? (
                      <input
                        type="text"
                        name="idProduto"
                        value={editConsumo?.idProduto || ""}
                        onChange={handleChange}
                      />
                    ) : (
                      consumo.idProduto
                    )}
                  </td>
                  <td>
                    {editIndex === index ? (
                      <input
                        type="text"
                        name="nomeProduto"
                        value={editConsumo?.nomeProduto || ""}
                        onChange={handleChange}
                      />
                    ) : (
                      consumo.nomeProduto
                    )}
                  </td>
                  <td>
                    {editIndex === index ? (
                      <input
                        type="text"
                        name="valor"
                        value={editConsumo?.valor || ""}
                        onChange={handleChange}
                      />
                    ) : (
                      consumo.valor
                    )}
                  </td>
                  <td>
                    {editIndex === index ? (
                      <button onClick={() => handleDelete(index)} style={actionButtonStyle}>
                        Deletar
                      </button>
                    ) : (
                      <button onClick={() => handleEdit(index)} style={actionButtonStyle}>
                        Editar
                      </button>
                    )}
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5}>Sem registros de consumo.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaConsumo;
