import React, { useState, useEffect } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

type Produto = {
  nomeProduto: string;
  preco: string;
  quantidade: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

// Estilos para o layout com menu à esquerda
const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "250px", // Largura do menu
  backgroundColor: "#000",
  padding: "20px",
  height: "100vh", // Altura para preencher a tela
  display: "flex",
  flexDirection: "column", // Organizar itens na vertical
  justifyContent: "flex-start", // Alinhar ao topo
};

const logoStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
  marginBottom: "20px", // Espaço abaixo da logo
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column", // Itens do menu em coluna
  gap: "15px",
};

const ListaProduto = () => {
  const [produtos, setProdutos] = useState<Produto[]>([]);
  const [isEditing, setIsEditing] = useState<boolean>(false);
  const [editIndex, setEditIndex] = useState<number>(-1);
  const [editNome, setEditNome] = useState<string>('');
  const [editPreco, setEditPreco] = useState<string>('');
  const [editQuantidade, setEditQuantidade] = useState<string>('');

  // Carregar produtos do localStorage quando o componente for montado
  useEffect(() => {
    const produtosSalvos = JSON.parse(localStorage.getItem('produtos') || '[]');
    if (produtosSalvos.length > 0) {
      setProdutos(produtosSalvos);
    }
  }, []);

  const handleEdit = (index: number) => {
    const produto = produtos[index];
    setIsEditing(true);
    setEditIndex(index);
    setEditNome(produto.nomeProduto);  // Corrigido para nomeProduto
    setEditPreco(produto.preco); // Corrigido para preco
    setEditQuantidade(produto.quantidade);
  };

  const handleSaveEdit = () => {
    const updatedProdutos = [...produtos];
    updatedProdutos[editIndex] = {
      nomeProduto: editNome,
      preco: editPreco,
      quantidade: editQuantidade
    };
    localStorage.setItem('produtos', JSON.stringify(updatedProdutos));
    setProdutos(updatedProdutos);
    setIsEditing(false);
    setEditIndex(-1);
    setEditNome('');
    setEditPreco('');
    setEditQuantidade('');
  };

  const handleCancelEdit = () => {
    setIsEditing(false);
    setEditIndex(-1);
    setEditNome('');
    setEditPreco('');
    setEditQuantidade('');
  };

  const handleDelete = (index: number) => {
    const updatedProdutos = [...produtos];
    updatedProdutos.splice(index, 1);
    localStorage.setItem('produtos', JSON.stringify(updatedProdutos));
    setProdutos(updatedProdutos);
    alert('Produto deletado com sucesso!');
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    const setter = {
      nome: setEditNome,
      preco: setEditPreco,
      quantidade: setEditQuantidade,
    }[name];

    if (setter) setter(value);
  };

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Lista de Produtos à Direita do Menu */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)" }}>
        <h5 className="center-align">Lista de Produtos</h5>
        {isEditing && (
          <div>
            <h6>Editar Produto</h6>
            <input
              type="text"
              name="nome"
              value={editNome}
              onChange={handleInputChange}
              placeholder="Nome do Produto"
            />
            <input
              type="text"
              name="preco"
              value={editPreco}
              onChange={handleInputChange}
              placeholder="Preço do Produto"
            />
            <input
              type="text"
              name="quantidade"
              value={editQuantidade}
              onChange={handleInputChange}
              placeholder="Quantidade do Produto"
            />
            <button onClick={handleSaveEdit} style={actionButtonStyle}>Salvar</button>
            <button onClick={handleCancelEdit} style={{ ...actionButtonStyle, backgroundColor: "#e74c3c" }}>Cancelar</button>
          </div>
        )}
        <table className="striped centered">
          <thead>
            <tr>
              <th>Produto</th>
              <th>Preço</th>
              <th>Quantidade</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {produtos.map((produto, index) => (
              <tr key={index}>
                <td>{produto.nomeProduto}</td>
                <td>{produto.preco}</td>
                <td>{produto.quantidade}</td>
                <td>
                  <button onClick={() => handleEdit(index)} style={actionButtonStyle}>Editar</button>
                  <button onClick={() => handleDelete(index)} style={actionButtonStyle}>Deletar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaProduto;
