import { useState } from "react";
import CSS from "csstype";
import editar from "../Assets/edit.png";
import excluir from "../Assets/delete.png";

const ImagemStyle: CSS.Properties = {
  padding: "5px",
  maxHeight: "30px",
  maxWidth: "30px",
};

export default function Servico({ servico, index, atualizarServicos }) {
  const [isEditing, setIsEditing] = useState(false);
  const [nome, setNome] = useState(servico.nome);
  const [valor, setValor] = useState(servico.valor);
  const [descricao, setDescricao] = useState(servico.descricao);

  const salvarEdicao = () => {
    const servicos = JSON.parse(localStorage.getItem("servicos") || "[]");
    servicos[index] = { nome, valor, descricao };
    localStorage.setItem("servicos", JSON.stringify(servicos));
    atualizarServicos(servicos);
    setIsEditing(false);
  };

  const cancelarEdicao = () => {
    setNome(servico.nome);
    setValor(servico.valor);
    setDescricao(servico.descricao);
    setIsEditing(false);
  };

  const deletarServico = () => {
    const servicos = JSON.parse(localStorage.getItem("servicos") || "[]");
    servicos.splice(index, 1);
    localStorage.setItem("servicos", JSON.stringify(servicos));
    atualizarServicos(servicos);
  };

  return (
    <tr>
      {isEditing ? (
        <>
          <td><input type="text" value={nome} onChange={(e) => setNome(e.target.value)} /></td>
          <td><input type="text" value={valor} onChange={(e) => setValor(e.target.value)} /></td>
          <td><input type="text" value={descricao} onChange={(e) => setDescricao(e.target.value)} /></td>
          <td>
            <button onClick={salvarEdicao}>Salvar</button>
            <button onClick={cancelarEdicao}>Cancelar</button>
          </td>
        </>
      ) : (
        <>
          <td>{servico.nome}</td>
          <td>{servico.valor}</td>
          <td>{servico.descricao}</td>
          <td>
            <img src={editar} style={ImagemStyle} alt="editar" onClick={() => setIsEditing(true)} />
            <img src={excluir} style={ImagemStyle} alt="excluir" onClick={deletarServico} />
          </td>
        </>
      )}
    </tr>
  );
}
