import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import React from "react";

// Estilos CSS - Agora usando o tipo correto do React
const backgroundColor: React.CSSProperties = {
  backgroundColor: "#000",
};

const botaoStyle: React.CSSProperties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: React.CSSProperties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

type Props = {
  tema: string;
};

const FormularioCadastroConsumo: React.FC<Props> = () => {
  const [clientes, setClientes] = useState([{ id: "1", nome: "Cliente 1" }, { id: "2", nome: "Cliente 2" }]);
  const [servicos, setServicos] = useState([{ id: "1", nome: "Serviço 1" }, { id: "2", nome: "Serviço 2" }]);
  const [produtos, setProdutos] = useState([{ id: "1", nome: "Produto 1" }, { id: "2", nome: "Produto 2" }]);

  const [selectedCliente, setSelectedCliente] = useState<string>("");
  const [selectedServico, setSelectedServico] = useState<string>("");
  const [selectedProduto, setSelectedProduto] = useState<string>("");
  const [precoServico, setPrecoServico] = useState<string>("");

  useEffect(() => {
    document.addEventListener("DOMContentLoaded", function () {
      var elems = document.querySelectorAll("select");
      M.FormSelect.init(elems);
    });
  }, []);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const novoConsumo = {
      cliente: selectedCliente,
      servico: selectedServico,
      produto: selectedProduto,
      preco: precoServico,
    };
    console.log(novoConsumo); // Aqui você pode enviar para o backend ou armazenar em um estado
  };

  return (
    <div>
      {/* Menu de Navegação */}
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Formulário de Cadastro */}
      <div className="container">
        <h4 className="center-align">Cadastrar Consumo</h4>
        <form className="col s12" onSubmit={handleSubmit}>
          <div className="row">
            <div className="input-field col s12">
              <select
                value={selectedCliente}
                onChange={(e) => setSelectedCliente(e.target.value)}
              >
                <option value="" disabled>Selecione o Cliente</option>
                {clientes.map((cliente) => (
                  <option key={cliente.id} value={cliente.id}>{cliente.nome}</option>
                ))}
              </select>
              <label>Cliente</label>
            </div>

            <div className="input-field col s12">
              <select
                value={selectedServico}
                onChange={(e) => setSelectedServico(e.target.value)}
              >
                <option value="" disabled>Selecione o Serviço</option>
                {servicos.map((servico) => (
                  <option key={servico.id} value={servico.id}>{servico.nome}</option>
                ))}
              </select>
              <label>Serviço</label>
            </div>

            <div className="input-field col s12">
              <select
                value={selectedProduto}
                onChange={(e) => setSelectedProduto(e.target.value)}
              >
                <option value="" disabled>Selecione o Produto</option>
                {produtos.map((produto) => (
                  <option key={produto.id} value={produto.id}>{produto.nome}</option>
                ))}
              </select>
              <label>Produto</label>
            </div>
          </div>

          <div className="input-field col s12">
            <input
              type="number"
              id="precoServico"
              value={precoServico}
              onChange={(e) => setPrecoServico(e.target.value)}
              step="0.01"
              min="0"
              required
            />
            <label htmlFor="precoServico">Preço do Serviço (R$)</label>
          </div>

          <div className="row">
            <div className="col s12">
              <button style={botaoStyle} type="submit">Registrar</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default FormularioCadastroConsumo;
