import React, { useState, useEffect } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

// Estilos CSS
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

interface Consumo {
  cliente: string;
  servico: string;
  produto: string;
  preco: number;
}

const ListaConsumos: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([]);
  const [novoConsumo, setNovoConsumo] = useState<Consumo>({
    cliente: "",
    servico: "",
    produto: "",
    preco: 0,
  });

  useEffect(() => {
    const consumos = JSON.parse(localStorage.getItem("consumos") || "[]");
    setConsumos(consumos);
  }, []);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setNovoConsumo((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
    const updatedConsumos = [...consumos, novoConsumo];
    localStorage.setItem("consumos", JSON.stringify(updatedConsumos));
    setConsumos(updatedConsumos);
    setNovoConsumo({ cliente: "", servico: "", produto: "", preco: 0 });
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">
            Cadastros
          </a>
          <a style={botaoStyle} href="/listaCliente">
            Cliente
          </a>
          <a style={botaoStyle} href="/listaProduto">
            Produto
          </a>
          <a style={botaoStyle} href="/listaPedidos">
            Consumo
          </a>
          <a style={botaoStyle} href="/listaServicos">
            Serviços
          </a>
          <a style={botaoStyle} href="/Listagens">
            Listagens
          </a>
        </div>
      </nav>

      <br />
      <div className="container">
        <h5 className="center-align">Lista de Consumos</h5>

        {/* Formulário de Adicionar Consumo */}
        <div>
          <input
            type="text"
            name="cliente"
            value={novoConsumo.cliente}
            onChange={handleInputChange}
            placeholder="Cliente"
          />
          <input
            type="text"
            name="servico"
            value={novoConsumo.servico}
            onChange={handleInputChange}
            placeholder="Serviço"
          />
          <input
            type="text"
            name="produto"
            value={novoConsumo.produto}
            onChange={handleInputChange}
            placeholder="Produto"
          />
          <input
            type="number"
            name="preco"
            value={novoConsumo.preco}
            onChange={handleInputChange}
            placeholder="Preço"
          />
          <button onClick={handleSubmit} style={botaoStyle}>
            Adicionar Consumo
          </button>
        </div>

        {/* Tabela de Consumos */}
        <table className="striped centered">
          <thead>
            <tr>
              <th>Cliente</th>
              <th>Serviço</th>
              <th>Produto</th>
              <th>Preço</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>{consumo.cliente}</td>
                  <td>{consumo.servico}</td>
                  <td>{consumo.produto}</td>
                  <td>{consumo.preco ? `R$ ${consumo.preco.toFixed(2)}` : "Preço não definido"}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={4}>Nenhum consumo cadastrado</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaConsumos;
