import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [clientes, setClientes] = useState([
    { id: 1, nome: "Cliente 1", cpf: "000.000.000-01", telefone: "(00)00000-0001", valorConsumo: 200 },
    { id: 2, nome: "Cliente 2", cpf: "000.000.000-02", telefone: "(00)00000-0002", valorConsumo: 300 },
    { id: 3, nome: "Cliente 3", cpf: "000.000.000-03", telefone: "(00)00000-0003", valorConsumo: 150 },
    { id: 4, nome: "Cliente 4", cpf: "000.000.000-04", telefone: "(00)00000-0004", valorConsumo: 50 },
    { id: 5, nome: "Cliente 5", cpf: "000.000.000-05", telefone: "(00)00000-0005", valorConsumo: 400 },
    { id: 6, nome: "Cliente 6", cpf: "000.000.000-06", telefone: "(00)00000-0006", valorConsumo: 100 },
    { id: 7, nome: "Cliente 7", cpf: "000.000.000-07", telefone: "(00)00000-0007", valorConsumo: 250 },
    { id: 8, nome: "Cliente 8", cpf: "000.000.000-08", telefone: "(00)00000-0008", valorConsumo: 350 },
    { id: 9, nome: "Cliente 9", cpf: "000.000.000-09", telefone: "(00)00000-0009", valorConsumo: 275 },
    { id: 10, nome: "Cliente 10", cpf: "000.000.000-10", telefone: "(00)00000-0010", valorConsumo: 125 },
  ]);

  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  // Ordenando clientes por valor de consumo em ordem decrescente
  const clientesOrdenados = [...clientes].sort((a, b) => b.valorConsumo - a.valorConsumo);

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
              <li className="tab col s2"><a href="#4" style={{ color: "black", fontSize: "10px" }}>Prod/Serv Consumo</a></li>
              <li className="tab col s2"><a href="#5" style={{ color: "black", fontSize: "10px" }}>Consumo Gênero</a></li>
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Valor de Consumo</th>
                    </tr>
                  </thead>
                  <tbody>
                    {clientesOrdenados.slice(0, 10).map((cliente) => (
                      <tr key={cliente.id}>
                        <td>{cliente.id}</td>
                        <td>{cliente.nome}</td>
                        <td>{cliente.cpf}</td>
                        <td>{cliente.telefone}</td>
                        <td>R$ {cliente.valorConsumo}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Outras abas permanecem inalteradas */}
          {/* Cliente Maior Qtd */}
          {/* Cliente Menor Consumo */}
          {/* Produto/Serviço Consumo */}
          {/* Consumo por Gênero */}
        </div>
      </div>
    </div>
  );
};

export default Listagens;
