import { useState, useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";
import React from "react";

const containerStyle: CSS.Properties = {
  display: "flex",
};
const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [editandoClienteId, setEditandoClienteId] = useState(null);
  const [novoNome, setNovoNome] = useState("");
  const [novoCpf, setNovoCpf] = useState("");
  const [novoTelefone, setNovoTelefone] = useState("");

  const handleEditarClick = (cliente) => {
    setEditandoClienteId(cliente.id);
    setNovoNome(cliente.nome);
    setNovoCpf(cliente.cpf);
    setNovoTelefone(cliente.telefone);
  };

  const handleSalvarEdicao = () => {
    console.log("Salvando cliente:", { id: editandoClienteId, nome: novoNome, cpf: novoCpf, telefone: novoTelefone });
    setEditandoClienteId(null); // Fecha o campo de edição
  };

  const handleCancelarEdicao = () => {
    setEditandoClienteId(null); // Fecha o campo de edição sem salvar
  };

  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Conteúdo principal */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
              <li className="tab col s2"><a href="#4" style={{ color: "black", fontSize: "10px" }}>Prod/Serv Consumo</a></li>
              <li className="tab col s2"><a href="#5" style={{ color: "black", fontSize: "10px" }}>Consumo Gênero</a></li>
            </ul>
          </div>

          <div id="1" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Valor Consumido</th>
                      <th>Ações</th>
                    </tr>
                  </thead>
                  <tbody>
                    {[
                      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
                      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
                      { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", valorConsumido: 800.00 },
                    ]
                      .map(cliente => (
                        <tr key={cliente.id}>
                          <td>{cliente.id}</td>
                          <td>{cliente.nome}</td>
                          <td>{cliente.cpf}</td>
                          <td>{cliente.telefone}</td>
                          <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                          <td>
                            <button
                              onClick={() => handleEditarClick(cliente)}
                              className="btn-small blue lighten-2"
                            >
                              Editar
                            </button>
                          </td>
                        </tr>
                      ))}
                  </tbody>
                </table>

                {editandoClienteId && (
                  <div style={{ marginTop: "20px" }}>
                    <h6>Editar Cliente</h6>
                    <div>
                      <label>Nome</label>
                      <input
                        type="text"
                        value={novoNome}
                        onChange={(e) => setNovoNome(e.target.value)}
                      />
                    </div>
                    <div>
                      <label>CPF</label>
                      <input
                        type="text"
                        value={novoCpf}
                        onChange={(e) => setNovoCpf(e.target.value)}
                      />
                    </div>
                    <div>
                      <label>Telefone</label>
                      <input
                        type="text"
                        value={novoTelefone}
                        onChange={(e) => setNovoTelefone(e.target.value)}
                      />
                    </div>
                    <div>
                      <button
                        type="button"
                        onClick={handleSalvarEdicao}
                        className="btn-small green lighten-2"
                      >
                        Salvar
                      </button>
                      <button
                        type="button"
                        onClick={handleCancelarEdicao}
                        className="btn-small red lighten-2"
                        style={{ marginLeft: "10px" }}
                      >
                        Cancelar
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
