import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

// Estilos do contêiner
const containerStyle: CSS.Properties = {
  display: "flex",
};

// Estilos para a barra de navegação
const navStyle: CSS.Properties = {
  width: "200px", // Largura do menu
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh", // Altura para preencher a tela
  display: "flex",
  flexDirection: "column", // Organiza os itens na vertical
  justifyContent: "flex-start", // Alinha ao topo
  position: "fixed", // Torna o menu fixo
  top: "0", // Fica no topo da página
  left: "0", // Alinha à esquerda
  bottom: "0", // Faz o menu ocupar toda a altura da tela
};

// Estilos do logo
const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px", // Espaço abaixo da logo
  textAlign: "center",
};

// Estilos dos links do menu
const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column", // Itens do menu em coluna
  gap: "10px",
};

// Estilo dos botões
const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

// Estilo para o botão de ação
const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000000",
  color: "#FFFFFF",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

// Estilo para o botão de cancelar
const cancelButtonStyle: CSS.Properties = {
  backgroundColor: "red", // Cor vermelha para o botão de cancelar
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

// Componente Listagens
const Listagens = () => {
  const [nomeFiltro, setNomeFiltro] = useState<string>("");

  useEffect(() => {
    // Inicializa os componentes do Materialize
    M.AutoInit();
    // Inicializa o menu lateral
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  const handleNomeChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setNomeFiltro(e.target.value);
  };

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Conteúdo principal */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        {/* Centralizando o título */}
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
              <li className="tab col s2"><a href="#4" style={{ color: "black", fontSize: "10px" }}>Prod/Serv Consumo</a></li>
              <li className="tab col s2"><a href="#5" style={{ color: "black", fontSize: "10px" }}>Consumo Gênero</a></li>
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Valor Consumido</th>
                    </tr>
                  </thead>
                  <tbody>
                    {[
                      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
                      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
                      { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", valorConsumido: 800.00 },
                      { id: 4, nome: "Ana Costa", cpf: "456.789.012-33", telefone: "(41)98765-4324", valorConsumido: 350.00 },
                      { id: 5, nome: "Ricardo Pereira", cpf: "567.890.123-44", telefone: "(51)98765-4325", valorConsumido: 120.00 },
                      { id: 6, nome: "Juliana Mendes", cpf: "678.901.234-55", telefone: "(61)98765-4326", valorConsumido: 900.00 },
                      { id: 7, nome: "Fernanda Silva", cpf: "789.012.345-66", telefone: "(71)98765-4327", valorConsumido: 200.00 },
                      { id: 8, nome: "Roberto Lima", cpf: "890.123.456-77", telefone: "(81)98765-4328", valorConsumido: 1000.00 },
                      { id: 9, nome: "Luciana Alves", cpf: "901.234.567-88", telefone: "(91)98765-4329", valorConsumido: 250.00 },
                      { id: 10, nome: "Ricardo Barbosa", cpf: "012.345.678-99", telefone: "(11)98765-4330", valorConsumido: 450.00 }
                    ]
                      .filter(cliente => cliente.valorConsumido > 300) // Filtrando valores consumidos maiores que 300
                      .sort((a, b) => b.valorConsumido - a.valorConsumido) // Ordenando do maior para o menor consumo
                      .map(cliente => (
                        <tr key={cliente.id}>
                          <td>{cliente.id}</td>
                          <td>{cliente.nome}</td>
                          <td>{cliente.cpf}</td>
                          <td>{cliente.telefone}</td>
                          <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Cliente Maior Qtd */}
          <div id="2" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Quantidade Consumida</th>
                    </tr>
                  </thead>
                  <tbody>
                    {[
                      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", qtdConsumida: 20 },
                      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", qtdConsumida: 15 },
                      { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", qtdConsumida: 10 },
                      { id: 4, nome: "Ana Costa", cpf: "456.789.012-33", telefone: "(41)98765-4324", qtdConsumida: 12 },
                      { id: 5, nome: "Ricardo Pereira", cpf: "567.890.123-44", telefone: "(51)98765-4325", qtdConsumida: 8 }
                    ]
                      .filter(cliente => cliente.qtdConsumida > 10) // Filtrando clientes com quantidade maior que 10
                      .sort((a, b) => b.qtdConsumida - a.qtdConsumida) // Ordenando da maior para a menor
                      .map(cliente => (
                        <tr key={cliente.id}>
                          <td>{cliente.id}</td>
                          <td>{cliente.nome}</td>
                          <td>{cliente.cpf}</td>
                          <td>{cliente.telefone}</td>
                          <td>{cliente.qtdConsumida}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Cliente Menor Consumo */}
          <div id="3" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>CPF</th>
                      <th>Telefone</th>
                      <th>Quantidade Consumida</th>
                    </tr>
                  </thead>
                  <tbody>
                    {[
                      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", qtdConsumida: 20 },
                      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", qtdConsumida: 15 },
                      { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", qtdConsumida: 10 },
                      { id: 4, nome: "Ana Costa", cpf: "456.789.012-33", telefone: "(41)98765-4324", qtdConsumida: 12 },
                      { id: 5, nome: "Ricardo Pereira", cpf: "567.890.123-44", telefone: "(51)98765-4325", qtdConsumida: 8 }
                    ]
                      .filter(cliente => cliente.qtdConsumida < 10) // Filtrando clientes com quantidade menor que 10
                      .sort((a, b) => a.qtdConsumida - b.qtdConsumida) // Ordenando da menor para a maior
                      .map(cliente => (
                        <tr key={cliente.id}>
                          <td>{cliente.id}</td>
                          <td>{cliente.nome}</td>
                          <td>{cliente.cpf}</td>
                          <td>{cliente.telefone}</td>
                          <td>{cliente.qtdConsumida}</td>
                        </tr>
                      ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
