import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";

const Listagens = () => {
  const [clientesValor, setClientesValor] = useState<any[]>([]);
  const [clientesQtd, setClientesQtd] = useState<any[]>([]);
  const [clientesMenor, setClientesMenor] = useState<any[]>([]);

  // Simulação de dados ou fetching de dados
  useEffect(() => {
    // Inicializa os componentes do Materialize
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);

    // Dados de exemplo (poderiam vir de uma API)
    setClientesValor([
      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
      { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", valorConsumido: 800.00 },
      // Outras entradas
    ]);

    setClientesQtd([
      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", qtdConsumida: 15 },
      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", qtdConsumida: 8 },
      // Outras entradas
    ]);

    setClientesMenor([
      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 150 },
      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 80 },
      // Outras entradas
    ]);
  }, []);

  const sortData = (data: any[], key: string) => {
    return [...data].sort((a, b) => b[key] - a[key]);
  };

  // Função para atualizar valores
  const updateValor = (id: number, newValor: number) => {
    const updatedClientes = clientesValor.map((cliente) =>
      cliente.id === id ? { ...cliente, valorConsumido: newValor } : cliente
    );
    setClientesValor(updatedClientes);
  };

  const updateQtd = (id: number, newQtd: number) => {
    const updatedClientes = clientesQtd.map((cliente) =>
      cliente.id === id ? { ...cliente, qtdConsumida: newQtd } : cliente
    );
    setClientesQtd(updatedClientes);
  };

  const deleteCliente = (id: number) => {
    setClientesValor(clientesValor.filter(cliente => cliente.id !== id));
    setClientesQtd(clientesQtd.filter(cliente => cliente.id !== id));
    setClientesMenor(clientesMenor.filter(cliente => cliente.id !== id));
  };

  return (
    <div className="container">
      <div className="row">
        <div className="col s12">
          <ul className="tabs">
            <li className="tab col s3"><a href="#1">Cliente Consumo Valor</a></li>
            <li className="tab col s2"><a href="#2">Cliente Maior Qtd</a></li>
            <li className="tab col s3"><a href="#3">Cliente Menor Consumo</a></li>
          </ul>
        </div>

        {/* Primeira Aba - Cliente Consumo Valor */}
        <div id="1" className="col s12">
          <div className="row">
            <div className="col s12">
              <h5>Ações</h5>
              <button className="btn waves-effect waves-light" onClick={() => console.log("Registrar cliente")}>Registrar</button>
              <button className="btn waves-effect waves-light" onClick={() => console.log("Editar cliente")}>Editar</button>
              <button className="btn waves-effect waves-light" onClick={() => console.log("Deletar cliente")}>Deletar</button>
            </div>
          </div>

          <table className="responsive-table centered">
            <thead>
              <tr>
                <th>Id</th>
                <th>Nome</th>
                <th>CPF</th>
                <th>Telefone</th>
                <th>Valor Consumido</th>
              </tr>
            </thead>
            <tbody>
              {sortData(clientesValor, "valorConsumido").map(cliente => (
                <tr key={cliente.id}>
                  <td>{cliente.id}</td>
                  <td>{cliente.nome}</td>
                  <td>{cliente.cpf}</td>
                  <td>{cliente.telefone}</td>
                  <td>
                    <input
                      type="number"
                      value={cliente.valorConsumido}
                      onChange={(e) => updateValor(cliente.id, parseFloat(e.target.value))}
                    />
                  </td>
                  <td>
                    <button className="btn red" onClick={() => deleteCliente(cliente.id)}>Deletar</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Segunda Aba - Cliente Maior Qtd */}
        <div id="2" className="col s12">
          <table className="responsive-table centered">
            <thead>
              <tr>
                <th>Id</th>
                <th>Nome</th>
                <th>CPF</th>
                <th>Telefone</th>
                <th>Quantidade Consumida</th>
              </tr>
            </thead>
            <tbody>
              {sortData(clientesQtd, "qtdConsumida").map(cliente => (
                <tr key={cliente.id}>
                  <td>{cliente.id}</td>
                  <td>{cliente.nome}</td>
                  <td>{cliente.cpf}</td>
                  <td>{cliente.telefone}</td>
                  <td>
                    <input
                      type="number"
                      value={cliente.qtdConsumida}
                      onChange={(e) => updateQtd(cliente.id, parseInt(e.target.value))}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Terceira Aba - Cliente Menor Consumo */}
        <div id="3" className="col s12">
          <table className="responsive-table centered">
            <thead>
              <tr>
                <th>Id</th>
                <th>Nome</th>
                <th>CPF</th>
                <th>Telefone</th>
                <th>Valor Consumido</th>
              </tr>
            </thead>
            <tbody>
              {sortData(clientesMenor, "valorConsumido").map(cliente => (
                <tr key={cliente.id}>
                  <td>{cliente.id}</td>
                  <td>{cliente.nome}</td>
                  <td>{cliente.cpf}</td>
                  <td>{cliente.telefone}</td>
                  <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
