import React, { useState, useEffect } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

const ListaClientes: React.FC = () => {
  const [clientes, setClientes] = useState<Cliente[]>(JSON.parse(localStorage.getItem("clientes") || "[]"));
  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(-1);
  const [editNome, setEditNome] = useState("");
  const [editCpf, setEditCpf] = useState("");
  const [editRg, setEditRg] = useState("");
  const [editDataEmissao, setEditDataEmissao] = useState("");
  const [editTelefone, setEditTelefone] = useState("");
  const [editDataCadastro, setEditDataCadastro] = useState("");
  const [editGenero, setEditGenero] = useState("");

  const handleEdit = (index: number) => {
    const cliente = clientes[index];
    setIsEditing(true);
    setEditIndex(index);
    setEditNome(cliente.nome);
    setEditCpf(cliente.cpf);
    setEditRg(cliente.rg);
    setEditDataEmissao(cliente.dataEmissao);
    setEditTelefone(cliente.telefone);
    setEditDataCadastro(cliente.dataCadastro);
    setEditGenero(cliente.genero);
  };

  const handleSaveEdit = () => {
    const updatedClientes = [...clientes];
    updatedClientes[editIndex] = {
      ...updatedClientes[editIndex],
      nome: editNome,
      cpf: editCpf,
      rg: editRg,
      dataEmissao: editDataEmissao,
      telefone: editTelefone,
      dataCadastro: editDataCadastro,
      genero: editGenero,
    };
    localStorage.setItem("clientes", JSON.stringify(updatedClientes));
    setClientes(updatedClientes);
    setIsEditing(false);
    setEditIndex(-1);
    setEditNome("");
    setEditCpf("");
    setEditRg("");
    setEditDataEmissao("");
    setEditTelefone("");
    setEditDataCadastro("");
    setEditGenero("");
  };

  const handleDelete = (index: number) => {
    const updatedClientes = [...clientes];
    updatedClientes.splice(index, 1);
    localStorage.setItem("clientes", JSON.stringify(updatedClientes));
    setClientes(updatedClientes);
    alert("Cliente deletado com sucesso!");
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    if (name === "editNome") setEditNome(value);
    if (name === "editCpf") setEditCpf(value);
    if (name === "editRg") setEditRg(value);
    if (name === "editDataEmissao") setEditDataEmissao(value);
    if (name === "editTelefone") setEditTelefone(value);
    if (name === "editDataCadastro") setEditDataCadastro(value);
    if (name === "editGenero") setEditGenero(value);
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>World Beauty</a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>
      <br />
      <div className="container">
        <h5 className="center-align">Lista de Clientes</h5>

        {isEditing && (
          <div>
            <h6>Editar Cliente</h6>
            <input type="text" name="editNome" value={editNome} onChange={handleInputChange} placeholder="Nome do Cliente" />
            <input type="text" name="editCpf" value={editCpf} onChange={handleInputChange} placeholder="CPF" />
            <input type="text" name="editRg" value={editRg} onChange={handleInputChange} placeholder="RG" />
            <input type="text" name="editDataEmissao" value={editDataEmissao} onChange={handleInputChange} placeholder="Data da Emissão" />
            <input type="text" name="editTelefone" value={editTelefone} onChange={handleInputChange} placeholder="Telefone" />
            <input type="text" name="editDataCadastro" value={editDataCadastro} onChange={handleInputChange} placeholder="Data de Cadastro" />
            <input type="text" name="editGenero" value={editGenero} onChange={handleInputChange} placeholder="Gênero" />

            <button onClick={handleSaveEdit} style={actionButtonStyle}>Salvar</button>
            <button onClick={() => setIsEditing(false)} style={actionButtonStyle}>Cancelar</button>
          </div>
        )}

        <table className="striped centered">
          <thead>
            <tr>
              <th>Nome</th>
              <th>CPF</th>
              <th>RG</th>
              <th>Data Emissão</th>
              <th>Telefone</th>
              <th>Data Cadastro</th>
              <th>Gênero</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {clientes.length > 0 ? (
              clientes.map((cliente, index) => (
                <tr key={index}>
                  <td>{cliente.nome}</td>
                  <td>{cliente.cpf}</td>
                  <td>{cliente.rg}</td>
                  <td>{cliente.dataEmissao}</td>
                  <td>{cliente.telefone}</td>
                  <td>{cliente.dataCadastro}</td>
                  <td>{cliente.genero}</td>
                  <td style={{ textAlign: "right" }}>
                    <button onClick={() => handleEdit(index)} style={actionButtonStyle}>Editar</button>
                    <button onClick={() => handleDelete(index)} style={actionButtonStyle}>Deletar</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={8}>Nenhum cliente cadastrado</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaClientes;
