import { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import CSS from "csstype";

type props = {
  tema: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

export default class FormularioCadastroConsumo extends Component<any, props> {
  state = {
    clienteId: "",
    servicoId: "",
    produtoId: "",
  };

  componentDidMount() {
    document.addEventListener("DOMContentLoaded", function () {
      var elems = document.querySelectorAll("select");
      M.FormSelect.init(elems);
    });
  }

  // Função para lidar com a mudança nos campos de seleção
  handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const { name, value } = e.target;
    this.setState({ [name]: value } as Pick<typeof this.state, keyof typeof this.state>);
  };

  // Função para lidar com o envio do formulário
  handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const { clienteId, servicoId, produtoId } = this.state;

    // Enviar os dados para o backend usando fetch ou axios
    try {
      const response = await fetch("/api/cadastroConsumo", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          clienteId,
          servicoId,
          produtoId,
        }),
      });

      if (response.ok) {
        // Redirecionar para a lista de consumos após o cadastro
        window.location.href = "/listaPedidos";
      } else {
        // Lidar com erros, se necessário
        console.error("Erro ao registrar consumo");
      }
    } catch (error) {
      console.error("Erro de rede", error);
    }
  };

  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            <a style={botaoStyle} href="/cadServico">
              Cadastros
            </a>
            <a style={botaoStyle} href="/listaCliente">
              Cliente
            </a>
            <a style={botaoStyle} href="/listaProduto">
              Produto
            </a>
            <a style={botaoStyle} href="/listaPedidos">
              Consumo
            </a>
            <a style={botaoStyle} href="/listaServicos">
              Serviços
            </a>
            <a style={botaoStyle} href="/Listagens">
              Listagens
            </a>
          </div>
        </nav>

        <br />
        <div className="container">
          <h4 className="center-align">Cadastrar Consumo</h4>
          <form onSubmit={this.handleSubmit} className="col s12 formCli">
            <div className="row">
              <div className="input-field col s12 opcoes">
                <select
                  name="clienteId"
                  value={this.state.clienteId}
                  onChange={this.handleChange}
                >
                  <option value="0">Selecione o Cliente</option>
                  <option value="1">Cliente - João Silva</option>
                  <option value="2">Cliente - Maria Souza</option>
                  <option value="3">Cliente - Ana Oliveira</option>
                  {/* Adicione os outros clientes aqui */}
                </select>
              </div>

              <div className="input-field col s12 opcoes">
                <select
                  name="servicoId"
                  value={this.state.servicoId}
                  onChange={this.handleChange}
                >
                  <option value="1">Selecione o Serviço</option>
                  <option value="1">Limpeza de Pele</option>
                  <option value="2">Hidratação Facial</option>
                  {/* Adicione os outros serviços aqui */}
                </select>
              </div>

              <div className="input-field col s12 opcoes">
                <select
                  name="produtoId"
                  value={this.state.produtoId}
                  onChange={this.handleChange}
                >
                  <option value="2">Selecione o Produto</option>
                  <option value="1">Shampoo Hidratante</option>
                  <option value="2">Creme de Hidratação Intensiva</option>
                  {/* Adicione os outros produtos aqui */}
                </select>
              </div>
            </div>
            <div className="row">
              <div className="col s12">
                <button
                  style={botaoStyle} // Aplica o estilo do botão
                  type="submit"
                  name="action"
                >
                  Registrar
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
