import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import CSS from "csstype";

const containerStyle: CSS.Properties = {
  display: "flex",
};
const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [clientesConsumoValor, setClientesConsumoValor] = useState([]);
  const [clientesMaiorQtd, setClientesMaiorQtd] = useState([]);
  const [clientesMenorConsumo, setClientesMenorConsumo] = useState([]);

  useEffect(() => {
    // Inicializa os componentes do Materialize
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);

    // Dados fictícios (substituir por dados reais ou APIs)
    const clientesValor = [
      { id: 1, nome: "João Silva", valorConsumido: 500.00 },
      { id: 2, nome: "Maria Oliveira", valorConsumido: 150.00 },
      { id: 3, nome: "Carlos Souza", valorConsumido: 800.00 },
      // Outros dados...
    ];

    const clientesQtd = [
      { id: 1, nome: "João Silva", qtdConsumida: 15 },
      { id: 2, nome: "Maria Oliveira", qtdConsumida: 8 },
      { id: 3, nome: "Carlos Souza", qtdConsumida: 20 },
      // Outros dados...
    ];

    setClientesConsumoValor(
      clientesValor.filter(cliente => cliente.valorConsumido > 300).sort((a, b) => b.valorConsumido - a.valorConsumido)
    );
    setClientesMaiorQtd(
      clientesQtd.sort((a, b) => b.qtdConsumida - a.qtdConsumida)
    );
    setClientesMenorConsumo(
      clientesValor.filter(cliente => cliente.valorConsumido < 150).sort((a, b) => a.valorConsumido - b.valorConsumido)
    );
  }, []);

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
              {/* Outros links */}
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            <table className="responsive-table centered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nome</th>
                  <th>Valor Consumido</th>
                </tr>
              </thead>
              <tbody>
                {clientesConsumoValor.map(cliente => (
                  <tr key={cliente.id}>
                    <td>{cliente.id}</td>
                    <td>{cliente.nome}</td>
                    <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Cliente Maior Qtd */}
          <div id="2" className="col s12">
            <table className="responsive-table centered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nome</th>
                  <th>Quantidade Consumida</th>
                </tr>
              </thead>
              <tbody>
                {clientesMaiorQtd.map(cliente => (
                  <tr key={cliente.id}>
                    <td>{cliente.id}</td>
                    <td>{cliente.nome}</td>
                    <td>{cliente.qtdConsumida}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* Cliente Menor Consumo */}
          <div id="3" className="col s12">
            <table className="responsive-table centered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nome</th>
                  <th>Valor Consumido</th>
                </tr>
              </thead>
              <tbody>
                {clientesMenorConsumo.map(cliente => (
                  <tr key={cliente.id}>
                    <td>{cliente.id}</td>
                    <td>{cliente.nome}</td>
                    <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
