import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import FormularioCadastroConsumo from "./cadConsumo";
import ListaConsumos from "./listConsumo";

const App = () => {
  const [consumos, setConsumos] = useState<any[]>([]);

  const handleCadastro = (novoConsumo: any) => {
    setConsumos([...consumos, novoConsumo]);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/cadConsumo"
          element={<FormularioCadastroConsumo onCadastro={handleCadastro} />}
        />
        <Route
          path="/listaConsumo"
          element={<ListaConsumos consumos={consumos} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
