import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";

const containerStyle = {
  display: "flex",
};

const navStyle = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [clientes, setClientes] = useState([]);
  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    // Inicializa os componentes do Materialize
    M.AutoInit();

    // Exemplo de dados (você pode buscar esses dados de uma API)
    setClientes([
      { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
      { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
      // Adicione outros clientes conforme necessário
    ]);
    setProdutos([
      { id: 1, nome: "Produto A", qtdConsumida: 20 },
      { id: 2, nome: "Produto B", qtdConsumida: 15 },
      // Adicione outros produtos conforme necessário
    ]);

    // Inicializa o menu lateral
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  // Função para renderizar as tabelas
  const renderTabela = (data, headers, actions = true) => (
    <table className="responsive-table centered">
      <thead>
        <tr>
          {headers.map((header) => (
            <th key={header}>{header}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item.id}>
            {Object.keys(item).map((key) => (
              key !== 'id' && <td key={key}>{item[key]}</td>
            ))}
            {actions && (
              <td>
                <button type="button" className="btn black white-text">
                  Editar
                </button>
                <button type="button" className="btn black white-text">
                  Deletar
                </button>
              </td>
            )}
          </tr>
        ))}
      </tbody>
    </table>
  );

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Conteúdo principal */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)" }}>
        {/* Centralizando o título */}
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <br />
        <div className="row">
          <div className="tabs-swipe-demo">
            <ul className="tabs">
              <li className="tab col s3"><a href="#1">Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2">Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3">Cliente Menor Consumo</a></li>
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            {renderTabela(
              clientes.filter(cliente => cliente.valorConsumido > 300).sort((a, b) => b.valorConsumido - a.valorConsumido),
              ["Id", "Nome", "CPF", "Telefone", "Valor Consumido", "Ações"]
            )}
          </div>

          {/* Cliente Maior Qtd */}
          <div id="2" className="col s12">
            {renderTabela(
              produtos.sort((a, b) => b.qtdConsumida - a.qtdConsumida),
              ["Id", "Nome", "Quantidade Consumida"]
            )}
          </div>

          {/* Cliente Menor Consumo */}
          <div id="3" className="col s12">
            {renderTabela(
              clientes.filter(cliente => cliente.valorConsumido < 150),
              ["Id", "Nome", "CPF", "Telefone", "Valor Consumido"]
            )}
          </div>
        </div>

        {/* Botão Registrar */}
        <div className="center">
          <button className="btn green">Registrar</button>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
