import { CSSProperties } from "react";
import editar from "../Assets/edit.png";
import excluir from "../Assets/delete.png";
import "materialize-css/dist/css/materialize.min.css";

type ConsumoItem = {
  descricao: string;
  preco: string;
};

type Props = {
  tema: string;
};

const consumoData: ConsumoItem[] = [
  { descricao: "Shampoo + Condicionador", preco: "R$ 30,00" },
  { descricao: "Escova + Hidratação", preco: "R$ 45,00" },
  { descricao: "Manicure Completa", preco: "R$ 25,00" },
  { descricao: "Depilação Completa", preco: "R$ 80,00" },
  { descricao: "Maquiagem Profissional", preco: "R$ 90,00" },
];

const backgroundColor: CSSProperties = {
  backgroundColor: "#000",
};

const botaoStyle: CSSProperties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSSProperties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const ImagemStyle: CSSProperties = {
  padding: "5px",
  maxHeight: "20px",
  maxWidth: "20px",
  cursor: "pointer",
};

function ListaConsumos(props: Props) {
  const handleEdit = (item: ConsumoItem) => {
    console.log("Editando item:", item.descricao);
    // Aqui você pode adicionar a lógica de edição (ex: abrir um modal ou redirecionar)
  };

  const handleDelete = (item: ConsumoItem) => {
    console.log("Excluindo item:", item.descricao);
    // Aqui você pode adicionar a lógica de exclusão (ex: chamar uma API para deletar)
  };

  return (
    <div>
      <nav>
        <div style={backgroundColor} className="nav-wrapper">
          <a className="brand-logo right" style={fontStyle}>
            World Beauty
          </a>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container">
        <h4 className="center-align">Lista de Consumos</h4>
        <table className="highlight centered responsive-table">
          <thead>
            <tr>
              <th>Descrição</th>
              <th>Preço</th>
              <th>Editar</th>
              <th>Excluir</th>
            </tr>
          </thead>
          <tbody>
            {consumoData.map((item, index) => (
              <tr key={index}>
                <td>{item.descricao}</td>
                <td>{item.preco}</td>
                <td>
                  <img
                    style={ImagemStyle}
                    onClick={() => handleEdit(item)}
                    src={editar}
                    alt="Editar"
                  />
                </td>
                <td>
                  <img
                    style={ImagemStyle}
                    onClick={() => handleDelete(item)}
                    src={excluir}
                    alt="Excluir"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default ListaConsumos;
