// cadProduto.tsx
import React, { Component } from "react";
import CSS from "csstype";

// Estilos para a página
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000000", // Preto
};

const botaoStyle: CSS.Properties = {
  padding: "12px 20px", // Ajuste no padding para botões maiores e mais confortáveis
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333", // Um tom de cinza para o fundo do botão
  color: "#FFFFFF", // Texto branco
  border: "none", // Removendo bordas para um visual mais limpo
  borderRadius: "5px", // Bordas arredondadas
  cursor: "pointer", // Indicação de clique
  transition: "background-color 0.3s ease", // Transição suave para o hover
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF", // Texto branco
};

// Componente de Cadastro de Produtos
class CadProduto extends Component {
  // Função para manipular o envio do formulário
  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const nomeProduto = (document.getElementById("nome_produto") as HTMLInputElement).value;
    const preco = (document.getElementById("preco") as HTMLInputElement).value;

    // Lógica para registrar o produto (aqui você pode fazer uma chamada de API ou salvar localmente)
    console.log("Produto registrado:", { nomeProduto, preco });
  };

  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo center" style={fontStyle}>
              World Beauty
            </a>

            {/* Links de navegação */}
            <a style={botaoStyle} href="/listaProduto">
              Produto
            </a>
            <a style={botaoStyle} href="/cadProduto">
              Cadastro de Produto
            </a>
            {/* Adicione outros links conforme necessário */}
          </div>
        </nav>

        <br />
        <div className="container">
          <div className="row">
            <form className="col s12" onSubmit={this.handleSubmit}>
              <div className="row">
                <div className="input-field col s6">
                  <input id="nome_produto" type="text" className="validate" />
                  <label htmlFor="nome_produto">Nome</label>
                </div>
                <div className="input-field col s6">
                  <input id="preco" type="text" className="validate" />
                  <label htmlFor="preco">Preço</label>
                </div>
              </div>
              <div className="row">
                <div className="col s12">
                  <button
                    className="btn waves-effect waves-light"
                    type="submit"
                    name="action"
                  >
                    Registrar
                    <i className="material-icons right">send</i>
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default CadProduto; // Exportando o componente
