import { useState, useEffect } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};
const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [clientes, setClientes] = useState([
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00, isEditing: false },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00, isEditing: false },
    // Outras entradas...
  ]);

  const [isRegistering, setIsRegistering] = useState(false);
  const [newCliente, setNewCliente] = useState({
    nome: "",
    cpf: "",
    telefone: "",
    valorConsumido: 0,
  });

  const handleEdit = (id: number) => {
    const updatedClientes = clientes.map(cliente => {
      if (cliente.id === id) {
        return { ...cliente, isEditing: true };
      }
      return cliente;
    });
    setClientes(updatedClientes);
  };

  const handleSave = (id: number, newName: string, newCpf: string, newTelefone: string, newValorConsumido: number) => {
    const updatedClientes = clientes.map(cliente => {
      if (cliente.id === id) {
        return { ...cliente, nome: newName, cpf: newCpf, telefone: newTelefone, valorConsumido: newValorConsumido, isEditing: false };
      }
      return cliente;
    });
    setClientes(updatedClientes);
    // Reordenar clientes por valor consumido (maior -> menor)
    setClientes((prevClientes) =>
      [...prevClientes].sort((a, b) => b.valorConsumido - a.valorConsumido)
    );
  };

  const handleCancel = (id: number) => {
    const updatedClientes = clientes.map(cliente => {
      if (cliente.id === id) {
        return { ...cliente, isEditing: false };
      }
      return cliente;
    });
    setClientes(updatedClientes);
  };

  const handleDelete = (id: number) => {
    const updatedClientes = clientes.filter(cliente => cliente.id !== id);
    setClientes(updatedClientes);
  };

  const handleRegister = () => {
    if (newCliente.nome && newCliente.cpf && newCliente.telefone) {
      setClientes([
        ...clientes,
        { ...newCliente, id: clientes.length + 1, isEditing: false }
      ]);
      setIsRegistering(false);
      setNewCliente({ nome: "", cpf: "", telefone: "", valorConsumido: 0 });
    } else {
      alert("Preencha todos os campos!");
    }
  };

  return (
    <div style={containerStyle}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
        </div>
      </nav>

      {/* Conteúdo principal */}
      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <h5>Listagens por Consumo</h5>
        <br />
        {/* Botão para registrar novo cliente */}
        <button
          style={{ backgroundColor: "blue", color: "white", marginBottom: "20px" }}
          onClick={() => setIsRegistering(!isRegistering)}
        >
          {isRegistering ? "Cancelar Registro" : "Registrar Novo Cliente"}
        </button>

        {isRegistering && (
          <div>
            <input
              type="text"
              placeholder="Nome"
              value={newCliente.nome}
              onChange={(e) => setNewCliente({ ...newCliente, nome: e.target.value })}
            />
            <input
              type="text"
              placeholder="CPF"
              value={newCliente.cpf}
              onChange={(e) => setNewCliente({ ...newCliente, cpf: e.target.value })}
            />
            <input
              type="text"
              placeholder="Telefone"
              value={newCliente.telefone}
              onChange={(e) => setNewCliente({ ...newCliente, telefone: e.target.value })}
            />
            <input
              type="number"
              placeholder="Valor Consumido"
              value={newCliente.valorConsumido}
              onChange={(e) => setNewCliente({ ...newCliente, valorConsumido: parseFloat(e.target.value) })}
            />
            <button
              style={{ backgroundColor: "green", color: "white", marginTop: "10px" }}
              onClick={handleRegister}
            >
              Registrar
            </button>
          </div>
        )}

        <div className="row">
          <div className="col s12">
            <table className="responsive-table centered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nome</th>
                  <th>CPF</th>
                  <th>Telefone</th>
                  <th>Valor Consumido</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {clientes.map(cliente => (
                  <tr key={cliente.id}>
                    <td>{cliente.id}</td>
                    <td>
                      {cliente.isEditing ? (
                        <input type="text" defaultValue={cliente.nome} />
                      ) : (
                        cliente.nome
                      )}
                    </td>
                    <td>
                      {cliente.isEditing ? (
                        <input type="text" defaultValue={cliente.cpf} />
                      ) : (
                        cliente.cpf
                      )}
                    </td>
                    <td>
                      {cliente.isEditing ? (
                        <input type="text" defaultValue={cliente.telefone} />
                      ) : (
                        cliente.telefone
                      )}
                    </td>
                    <td>
                      {cliente.isEditing ? (
                        <input type="number" defaultValue={cliente.valorConsumido} />
                      ) : (
                        `R$ ${cliente.valorConsumido.toFixed(2)}`
                      )}
                    </td>
                    <td>
                      {cliente.isEditing ? (
                        <>
                          <button
                            style={{ backgroundColor: "green", color: "white" }}
                            onClick={() => handleSave(cliente.id, 'Novo Nome', 'Novo CPF', 'Novo Telefone', 600)}
                          >
                            Salvar
                          </button>
                          <button
                            style={{ backgroundColor: "red", color: "white", marginLeft: "10px" }}
                            onClick={() => handleCancel(cliente.id)}
                          >
                            Cancelar
                          </button>
                        </>
                      ) : (
                        <>
                          <button
                            style={{ backgroundColor: "#000", color: "white" }}
                            onClick={() => handleEdit(cliente.id)}
                          >
                            Editar
                          </button>
                          <button
                            style={{ backgroundColor: "red", color: "white", marginLeft: "10px" }}
                            onClick={() => window.confirm('Tem certeza que deseja deletar?') && handleDelete(cliente.id)}
                          >
                            Deletar
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Listagens;
