import React, { Component } from 'react';
import CSS from 'csstype';

const actionButtonStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

export default class EditarCliente extends Component {
  state = {
    editNome: "",
    editCpf: "",
    editRg: "",
    editDataEmissao: "",
    editTelefone: "",
    editDataCadastro: "",
    editGenero: "",
    isEditing: true,
  };

  handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleSaveEdit = (e: React.FormEvent) => {
    e.preventDefault();
    // Lógica para salvar a edição, por exemplo, atualizar no localStorage ou no banco de dados.
    alert("Cliente atualizado!");
  };

  render() {
    const { editNome, editCpf, editRg, editDataEmissao, editTelefone, editDataCadastro, editGenero, isEditing } = this.state;

    return (
      <div>
        <h6>Editar Cliente</h6>
        <input
          type="text"
          name="editNome"
          value={editNome}
          onChange={this.handleInputChange}
          placeholder="Nome do Cliente"
        />
        <input
          type="text"
          name="editCpf"
          value={editCpf}
          onChange={this.handleInputChange}
          placeholder="CPF"
        />
        <input
          type="text"
          name="editRg"
          value={editRg}
          onChange={this.handleInputChange}
          placeholder="RG"
        />
        <input
          type="text"
          name="editDataEmissao"
          value={editDataEmissao}
          onChange={this.handleInputChange}
          placeholder="Data da Emissão"
        />
        <input
          type="text"
          name="editTelefone"
          value={editTelefone}
          onChange={this.handleInputChange}
          placeholder="Telefone"
        />
        <input
          type="text"
          name="editDataCadastro"
          value={editDataCadastro}
          onChange={this.handleInputChange}
          placeholder="Data de Cadastro"
        />

        {/* Seção de Gênero com radio buttons */}
        <div className="input-field">
          <label>Gênero:</label>
          <div className="row" style={{ paddingLeft: "20px", marginLeft: "40px" }}>
            <div className="col s12">
              <label>
                <input
                  name="editGenero"
                  type="radio"
                  value="Masculino"
                  checked={editGenero === "Masculino"}
                  onChange={this.handleInputChange}
                />
                <span>Masculino</span>
              </label>
            </div>
            <div className="col s12">
              <label>
                <input
                  name="editGenero"
                  type="radio"
                  value="Feminino"
                  checked={editGenero === "Feminino"}
                  onChange={this.handleInputChange}
                />
                <span>Feminino</span>
              </label>
            </div>
            <div className="col s12">
              <label>
                <input
                  name="editGenero"
                  type="radio"
                  value="Outro"
                  checked={editGenero === "Outro"}
                  onChange={this.handleInputChange}
                />
                <span>Outro</span>
              </label>
            </div>
          </div>
        </div>

        <button onClick={this.handleSaveEdit} style={actionButtonStyle}>Salvar</button>
        <button onClick={() => this.setState({ isEditing: false })} style={actionButtonStyle}>Cancelar</button>
      </div>
    );
  }
}
