import React, { useState } from 'react';

function ClienteConsumo() {
  // Estado para armazenar os dados dos clientes
  const [clientes, setClientes] = useState([
    { id: 1, nome: "João Silva", cpf: "123.456.789-00", telefone: "(11)98765-4321", valorConsumido: 500.00 },
    { id: 2, nome: "Maria Oliveira", cpf: "234.567.890-11", telefone: "(21)98765-4322", valorConsumido: 150.00 },
    { id: 3, nome: "Carlos Souza", cpf: "345.678.901-22", telefone: "(31)98765-4323", valorConsumido: 800.00 },
    { id: 4, nome: "Ana Costa", cpf: "456.789.012-33", telefone: "(41)98765-4324", valorConsumido: 350.00 },
    { id: 5, nome: "Ricardo Pereira", cpf: "567.890.123-44", telefone: "(51)98765-4325", valorConsumido: 120.00 },
    { id: 6, nome: "Juliana Mendes", cpf: "678.901.234-55", telefone: "(61)98765-4326", valorConsumido: 900.00 },
    { id: 7, nome: "Fernanda Silva", cpf: "789.012.345-66", telefone: "(71)98765-4327", valorConsumido: 200.00 },
    { id: 8, nome: "Roberto Lima", cpf: "890.123.456-77", telefone: "(81)98765-4328", valorConsumido: 1000.00 },
    { id: 9, nome: "Luciana Alves", cpf: "901.234.567-88", telefone: "(91)98765-4329", valorConsumido: 250.00 },
    { id: 10, nome: "Ricardo Barbosa", cpf: "012.345.678-99", telefone: "(11)98765-4330", valorConsumido: 450.00 }
  ]);

  const [formData, setFormData] = useState({ id: '', nome: '', cpf: '', telefone: '', valorConsumido: '' });
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  // Função para manipular alterações no formulário
  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormData(prevState => ({ ...prevState, [id]: value }));
  };

  // Função para registrar ou editar um cliente
  const handleSave = () => {
    if (formData.id) {
      // Editando cliente existente
      setClientes(prevClientes =>
        prevClientes.map(cliente =>
          cliente.id === formData.id ? { ...formData } : cliente
        )
      );
    } else {
      // Adicionando novo cliente
      const newId = clientes.length ? Math.max(...clientes.map(cliente => cliente.id)) + 1 : 1;
      setClientes([...clientes, { ...formData, id: newId }]);
    }
    setShowRegisterForm(false);
    setFormData({ id: '', nome: '', cpf: '', telefone: '', valorConsumido: '' });
  };

  // Função para abrir o formulário para adicionar um novo cliente
  const handleRegister = () => {
    setFormData({ id: '', nome: '', cpf: '', telefone: '', valorConsumido: '' });
    setShowRegisterForm(true);
  };

  // Função para editar cliente
  const handleEdit = (cliente) => {
    setFormData(cliente);
    setShowRegisterForm(true);
  };

  // Função para deletar um cliente
  const handleDelete = (id) => {
    setClientes(clientes.filter(cliente => cliente.id !== id));
  };

  return (
    <div>
      <div id="1" className="col s12">
        <form>
          <div>
            <table className="responsive-table centered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Nome</th>
                  <th>CPF</th>
                  <th>Telefone</th>
                  <th>Valor Consumido</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {clientes
                  .filter(cliente => cliente.valorConsumido > 300) // Filtrando valores consumidos maiores que 300
                  .sort((a, b) => b.valorConsumido - a.valorConsumido) // Ordenando do maior para o menor consumo
                  .map(cliente => (
                    <tr key={cliente.id}>
                      <td>{cliente.id}</td>
                      <td>{cliente.nome}</td>
                      <td>{cliente.cpf}</td>
                      <td>{cliente.telefone}</td>
                      <td>R$ {cliente.valorConsumido.toFixed(2)}</td>
                      <td>
                        <button type="button" className="btn black white-text" onClick={() => handleEdit(cliente)}>
                          Editar
                        </button>
                        <button type="button" className="btn black white-text" onClick={() => handleDelete(cliente.id)}>
                          Deletar
                        </button>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </form>

        <div className="center">
          <button className="btn green" onClick={handleRegister}>Registrar</button>
        </div>
      </div>

      {/* Formulário de Registro ou Edição */}
      {showRegisterForm && (
        <div>
          <h4>{formData.id ? 'Editar Cliente' : 'Registrar Cliente'}</h4>
          <div className="input-field">
            <input
              type="text"
              id="nome"
              value={formData.nome}
              onChange={handleInputChange}
              placeholder="Nome"
            />
          </div>
          <div className="input-field">
            <input
              type="text"
              id="cpf"
              value={formData.cpf}
              onChange={handleInputChange}
              placeholder="CPF"
            />
          </div>
          <div className="input-field">
            <input
              type="text"
              id="telefone"
              value={formData.telefone}
              onChange={handleInputChange}
              placeholder="Telefone"
            />
          </div>
          <div className="input-field">
            <input
              type="number"
              id="valorConsumido"
              value={formData.valorConsumido}
              onChange={handleInputChange}
              placeholder="Valor Consumido"
            />
          </div>
          <div className="center">
            <button className="btn green" onClick={handleSave}>Salvar</button>
            <button className="btn red" onClick={() => setShowRegisterForm(false)}>Cancelar</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ClienteConsumo;
