import { Component } from "react";
import CSS from "csstype";

// Estilos em CSS inline
const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
  marginRight: "5px",
};

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  dataNascimento: string;
  genero: string;
};

type CadastroClienteProps = {
  cliente?: Cliente;
  onSave: (cliente: Cliente) => void;
  onCancel: () => void;
};

export default class CadastroCliente extends Component<CadastroClienteProps> {
  state = {
    nome: this.props.cliente?.nome || "",
    cpf: this.props.cliente?.cpf || "",
    rg: this.props.cliente?.rg || "",
    dataEmissao: this.props.cliente?.dataEmissao || "",
    telefone: this.props.cliente?.telefone || "",
    dataCadastro: this.props.cliente?.dataCadastro || "",
    dataNascimento: this.props.cliente?.dataNascimento || "",
    genero: this.props.cliente?.genero || "",
  };

  handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    this.setState({ [name]: value } as any);
  };

  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const cliente: Cliente = {
      id: this.props.cliente?.id || String(Date.now()),
      nome: this.state.nome,
      cpf: this.state.cpf,
      rg: this.state.rg,
      dataEmissao: this.state.dataEmissao,
      telefone: this.state.telefone,
      dataCadastro: this.state.dataCadastro,
      dataNascimento: this.state.dataNascimento,
      genero: this.state.genero,
    };

    this.props.onSave(cliente);

    this.setState({
      nome: "",
      cpf: "",
      rg: "",
      dataEmissao: "",
      telefone: "",
      dataCadastro: "",
      dataNascimento: "",
      genero: "",
    });
  };

  render() {
    const { nome, cpf, rg, dataEmissao, telefone, dataCadastro, dataNascimento, genero } = this.state;

    return (
      <form onSubmit={this.handleSubmit}>
        <div className="input-field">
          <input type="text" name="nome" value={nome} onChange={this.handleChange} placeholder="Nome do Cliente" className="validate" />
        </div>
        <div className="input-field">
          <input type="text" name="cpf" value={cpf} onChange={this.handleChange} placeholder="CPF" className="validate" />
        </div>
        <div className="input-field">
          <input type="text" name="rg" value={rg} onChange={this.handleChange} placeholder="RG" className="validate" />
        </div>
        <div className="input-field">
          <input type="text" name="dataEmissao" value={dataEmissao} onChange={this.handleChange} placeholder="Data de Emiss√£o" className="validate" />
        </div>
        <div className="input-field">
          <input type="text" name="telefone" value={telefone} onChange={this.handleChange} placeholder="Telefone" className="validate" />
        </div>
        <div className="input-field">
          <input type="text" name="dataCadastro" value={dataCadastro} onChange={this.handleChange} placeholder="Data de Cadastro" className="validate" />
        </div>
        <div className="input-field">
          <input type="text" name="dataNascimento" value={dataNascimento} onChange={this.handleChange} placeholder="Data de Nascimento" className="validate" />
        </div>
        <div className="input-field">
          <select name="genero" value={genero} onChange={this.handleChange} className="browser-default">
            <option value="" disabled>Escolha o G√™nero</option>
            <option value="Masculino">Masculino</option>
            <option value="Feminino">Feminino</option>
            <option value="Outro">Outro</option>
          </select>
        </div>

        {/* Bot√µes para Salvar ou Cancelar */}
        <button type="submit" style={botaoStyle}>Salvar</button>
        <button type="button" onClick={this.props.onCancel} style={botaoStyle}>Cancelar</button>
      </form>
    );
  }
}
