import { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import CSS from "csstype";

type Props = {
  tema: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

interface State {
  cliente: string;
  servico: string;
  produto: string;
}

export default class FormularioCadastroConsumo extends Component<any, State> {
  state = {
    cliente: "",
    servico: "",
    produto: "",
  };

  componentDidMount() {
    document.addEventListener("DOMContentLoaded", function () {
      var elems = document.querySelectorAll("select");
      M.FormSelect.init(elems);
    });
  }

  handleChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Enviar os dados selecionados para a próxima página, pode ser via redirecionamento ou outro método
    console.log(this.state);
    // Aqui você pode redirecionar ou salvar os dados no backend
  };

  render() {
    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            {/* Links de navegação */}
          </div>
        </nav>

        <br />
        <div className="container">
          <h4 className="center-align">Cadastrar Consumo</h4>
          <form className="col s12 formCli" onSubmit={this.handleSubmit}>
            <div className="row">
              <div className="input-field col s12 opcoes">
                <select
                  name="cliente"
                  value={this.state.cliente}
                  onChange={this.handleChange}
                >
                  <option value="">Selecione o Cliente</option>
                  <option value="1">Cliente - João Silva</option>
                  <option value="2">Cliente - Maria Souza</option>
                  {/* Outros clientes */}
                </select>
              </div>

              <div className="input-field col s12 opcoes">
                <select
                  name="servico"
                  value={this.state.servico}
                  onChange={this.handleChange}
                >
                  <option value="">Selecione o Serviço</option>
                  <option value="1">Limpeza de Pele</option>
                  <option value="2">Hidratação Facial</option>
                  {/* Outros serviços */}
                </select>
              </div>

              <div className="input-field col s12 opcoes">
                <select
                  name="produto"
                  value={this.state.produto}
                  onChange={this.handleChange}
                >
                  <option value="">Selecione o Produto</option>
                  <option value="1">Shampoo Hidratante</option>
                  <option value="2">Creme de Hidratação Intensiva</option>
                  {/* Outros produtos */}
                </select>
              </div>
            </div>
            <div className="row">
              <div className="col s12">
                <button
                  style={botaoStyle}
                  type="submit"
                  name="action"
                >
                  Registrar
                </button>
              </div>
            </div>
          </form>
        </div>
      </div>
    );
  }
}
