import { useEffect, useState } from "react";
import "materialize-css/dist/css/materialize.min.css";
import CSS from "csstype";
import M from "materialize-css";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const Listagens = () => {
  const [clientes, setClientes] = useState([
    { id: 1, nome: "Ana Silva", cpf: "123.456.789-01", telefone: "(11)98765-4321", valorConsumo: 200, quantidadeConsumida: 15 },
    // Outros clientes...
  ]);

  const [produtosServicos, setProdutosServicos] = useState([
    { id: 1, nome: "Serviço de Massagem", preco: 150 },
    { id: 2, nome: "Tratamento Facial", preco: 120 },
    { id: 3, nome: "Corte de Cabelo", preco: 90 },
    { id: 4, nome: "Manicure e Pedicure", preco: 70 },
    { id: 5, nome: "Maquiagem Profissional", preco: 200 },
  ]);

  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
  }, []);

  const clientesOrdenadosPorConsumoAsc = [...clientes].sort((a, b) => a.valorConsumo - b.valorConsumo);
  const clientesOrdenadosPorConsumoDesc = [...clientes].sort((a, b) => b.valorConsumo - a.valorConsumo);
  const clientesOrdenadosPorQuantidade = [...clientes].sort((a, b) => b.quantidadeConsumida - a.quantidadeConsumida);
  const produtosServicosOrdenados = [...produtosServicos].sort((a, b) => b.preco - a.preco);

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div className="container" style={{ marginLeft: "270px", width: "calc(100% - 270px)", justifyContent: "center", alignItems: "center" }}>
        <div style={{ display: "flex", justifyContent: "center", alignItems: "center", height: "100px" }}>
          <h5>Listagens por Consumo</h5>
        </div>

        <div className="row">
          <div className="">
            <ul className="tabs-swipe-demo tabs tabsConsumo">
              <li className="tab col s3"><a href="#1" style={{ color: "black", fontSize: "10px" }}>Cliente Consumo Valor</a></li>
              <li className="tab col s2"><a href="#2" style={{ color: "black", fontSize: "10px" }}>Cliente Maior Qtd</a></li>
              <li className="tab col s3"><a href="#3" style={{ color: "black", fontSize: "10px" }}>Cliente Menor Consumo</a></li>
              <li className="tab col s2"><a href="#4" style={{ color: "black", fontSize: "10px" }}>Prod/Serv Consumo</a></li>
              <li className="tab col s2"><a href="#5" style={{ color: "black", fontSize: "10px" }}>Consumo Gênero</a></li>
            </ul>
          </div>

          {/* Cliente Consumo Valor */}
          <div id="1" className="col s12">
            {/* Conteúdo da aba "Cliente Consumo Valor" */}
          </div>

          {/* Cliente Maior Qtd */}
          <div id="2" className="col s12">
            {/* Conteúdo da aba "Cliente Maior Qtd" */}
          </div>

          {/* Cliente Menor Consumo */}
          <div id="3" className="col s12">
            {/* Conteúdo da aba "Cliente Menor Consumo" */}
          </div>

          {/* Prod/Serv Consumo */}
          <div id="4" className="col s12">
            <form>
              <div>
                <table className="responsive-table centered">
                  <thead>
                    <tr>
                      <th>Id</th>
                      <th>Nome</th>
                      <th>Preço</th>
                    </tr>
                  </thead>
                  <tbody>
                    {produtosServicosOrdenados.map((produto) => (
                      <tr key={produto.id}>
                        <td>{produto.id}</td>
                        <td>{produto.nome}</td>
                        <td>R$ {produto.preco}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </form>
          </div>

          {/* Outros tabs podem ser adicionados conforme necessário */}
        </div>
      </div>
    </div>
  );
};

export default Listagens;
