// App.tsx
import React from 'react';
import { BrowserRouter, Navigate, Route, Routes } from "react-router-dom";
import PaginaInicial from "./paginaInicial";
import ListaCliente from "./listCliente";
import FormularioCadastroCliente from "./cadCliente";
import ListaProduto from "./listProduto";
import FormularioCadastroProduto from "./cadProduto";
import FormularioCadastroServico from "./cadServico";
import FormularioCadastroConsumo from "./cadConsumo";
import Produtos from "./produto";
import ListaPedidos from "./listConsumo"; // Verifique o nome do arquivo aqui
import Pedidos from "./consumo";          // Verifique o nome do arquivo aqui
import Clientes from "./cliente";
import Servicos from "./servico";
import ListaServicos from "./listServico";
import Listagens from "./listagens";

function App() {
  // Exemplo de lista de pedidos, que pode vir de um estado ou API
  const pedidos = [
    { id: 1, nomeProduto: "Produto 1", valor: 100, nomeServico: "Serviço 1", idProduto: 1, idServico: 1 },
    { id: 2, nomeProduto: "Produto 2", valor: 200, nomeServico: "Serviço 2", idProduto: 2, idServico: 2 }
  ];

  // Função de editar pedido
  const handleEditar = (id: number) => {
    console.log(`Editando pedido com id: ${id}`);
  };

  // Função de excluir pedido
  const handleExcluir = (id: number) => {
    console.log(`Excluindo pedido com id: ${id}`);
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route index element={<PaginaInicial />} />
        <Route path="*" element={<Navigate to="/" />} />
        <Route path="/Listagens" element={<Listagens />} />
        <Route path="/listaCliente" element={<ListaCliente />} />
        <Route path="/listaProduto" element={<ListaProduto />} />
        <Route
          path="/listaPedidos"
          element={<ListaPedidos pedidos={pedidos} onEditar={handleEditar} onExcluir={handleExcluir} />}
        />
        <Route path="/listaServicos" element={<ListaServicos />} />
        <Route path="/formularioCadastroCliente" element={<FormularioCadastroCliente />} />
        <Route path="/formularioCadastroProduto" element={<FormularioCadastroProduto />} />
        <Route path="/formularioCadastroServico" element={<FormularioCadastroServico />} />
        <Route path="/formularioCadastroPedido" element={<FormularioCadastroConsumo />} />
        <Route path="/produtos" element={<Produtos />} />
        <Route path="/pedidos" element={<Pedidos />} />
        <Route path="/clientes" element={<Clientes />} />
        <Route path="/servicos" element={<Servicos />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
