import React, { useEffect, useState } from 'react';
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css";
import api from "../services/api";

// Definindo tipos para os dados
type Cliente = {
  id: number;
  nome: string;
  cpf: string;
  telefone: string;
  valorConsumido: number;
  qtdConsumida: number;
};

type ProdutoServico = {
  id: number;
  nome: string;
  valor: number;
};

type ConsumoGenero = {
  id: number;
  nome: string;
  produto: string;
  valor: number;
};

const Listagens: React.FC = () => {
  const [clientesValor, setClientesValor] = useState<Cliente[]>([]);
  const [clientesQtd, setClientesQtd] = useState<Cliente[]>([]);
  const [clientesMenorConsumo, setClientesMenorConsumo] = useState<Cliente[]>([]);
  const [produtosServicos, setProdutosServicos] = useState<ProdutoServico[]>([]);
  const [consumoFeminino, setConsumoFeminino] = useState<ConsumoGenero[]>([]);
  const [consumoMasculino, setConsumoMasculino] = useState<ConsumoGenero[]>([]);

  useEffect(() => {
    M.AutoInit();
    const elems = document.querySelectorAll(".sidenav");
    M.Sidenav.init(elems);
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      // Simulando chamadas de API. Substitua estas com chamadas reais na sua implementação
      const responseClientesValor = await fetch('/api/clientes-valor');
      const clientesValorData = await responseClientesValor.json();
      setClientesValor(clientesValorData);

      const responseClientesQtd = await fetch('/api/clientes-qtd');
      const clientesQtdData = await responseClientesQtd.json();
      setClientesQtd(clientesQtdData);

      const responseClientesMenorConsumo = await fetch('/api/clientes-menor-consumo');
      const clientesMenorConsumoData = await responseClientesMenorConsumo.json();
      setClientesMenorConsumo(clientesMenorConsumoData);

      const responseProdutosServicos = await fetch('/api/produtos-servicos');
      const produtosServicosData = await responseProdutosServicos.json();
      setProdutosServicos(produtosServicosData);

      const responseConsumoFeminino = await fetch('/api/consumo-feminino');
      const consumoFemininoData = await responseConsumoFeminino.json();
      setConsumoFeminino(consumoFemininoData);

      const responseConsumoMasculino = await fetch('/api/consumo-masculino');
      const consumoMasculinoData = await responseConsumoMasculino.json();
      setConsumoMasculino(consumoMasculinoData);

    } catch (error) {
      console.error("Erro ao buscar dados:", error);
    }
  };

  return (
    <div style={{ display: "flex" }}>
      {/* Menu de Navegação à Esquerda */}
      <nav style={{ width: "200px", backgroundColor: "#000000", padding: "10px", height: "100vh", display: "flex", flexDirection: "column", justifyContent: "flex-start", position: "fixed", top: "0", left: "0", bottom: "0" }}>
        <a style={{ fontSize: "large", fontFamily: "Arial, sans-serif", color: "#FFFFFF", marginBottom: "20px", textAlign: "center" }}>World Beauty</a>
        <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          <a style={{ padding: "8px 16px", fontFamily: "Arial, sans-serif", backgroundColor: "#333333", color: "#FFFFFF", border: "none", borderRadius: "5px", textAlign: "center", cursor: "pointer" }} href="/cadServico">Cadastros</a>
          <a style={{ padding: "8px 16px", fontFamily: "Arial, sans-serif", backgroundColor: "#333333", color: "#FFFFFF", border: "none", borderRadius: "5px", textAlign: "center", cursor: "pointer" }} href="/listaCliente">Cliente</a>
          <a style={{ padding: "8px 16px", fontFamily: "Arial, sans-serif", backgroundColor: "#333333", color: "#FFFFFF", border: "none", borderRadius: "5px", textAlign: "center", cursor: "pointer" }} href="/listaProduto">Produto</a>
          <a style={{ padding: "8px 16px", fontFamily: "Arial, sans-serif", backgroundColor: "#333333", color: "#FFFFFF", border: "none", borderRadius: "5px", textAlign: "center", cursor: "pointer" }} href="/listaPedidos">Consumo</a>
        </div>
      </nav>

      {/* Conteúdo da Página */}
      <div style={{ marginLeft: "220px", padding: "20px" }}>
        <h1>Clientes com Maior Valor Consumido</h1>
        <ul>
          {clientesValor.map(cliente => (
            <li key={cliente.id}>{cliente.nome} - {cliente.valorConsumido}</li>
          ))}
        </ul>

        <h1>Clientes com Maior Quantidade Consumida</h1>
        <ul>
          {clientesQtd.map(cliente => (
            <li key={cliente.id}>{cliente.nome} - {cliente.qtdConsumida}</li>
          ))}
        </ul>

        <h1>Clientes com Menor Consumo</h1>
        <ul>
          {clientesMenorConsumo.map(cliente => (
            <li key={cliente.id}>{cliente.nome} - {cliente.valorConsumido}</li>
          ))}
        </ul>

        <h1>Produtos e Serviços</h1>
        <ul>
          {produtosServicos.map(produto => (
            <li key={produto.id}>{produto.nome} - {produto.valor}</li>
          ))}
        </ul>

        <h1>Consumo Feminino</h1>
        <ul>
          {consumoFeminino.map(consumo => (
            <li key={consumo.id}>{consumo.nome} - {consumo.produto} - {consumo.valor}</li>
          ))}
        </ul>

        <h1>Consumo Masculino</h1>
        <ul>
          {consumoMasculino.map(consumo => (
            <li key={consumo.id}>{consumo.nome} - {consumo.produto} - {consumo.valor}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Listagens;
