import React, { useState, useEffect } from "react";
import CSS from "csstype";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "200px",
  backgroundColor: "#000000",
  padding: "10px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  position: "fixed",
  top: "0",
  left: "0",
  bottom: "0",
};

const logoStyle: CSS.Properties = {
  fontSize: "large",
  fontFamily: "Arial, sans-serif",
  color: "#FFFFFF",
  marginBottom: "20px",
  textAlign: "center",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "10px",
};

const botaoStyle: CSS.Properties = {
  padding: "8px 16px",
  fontFamily: "Arial, sans-serif",
  backgroundColor: "#333333",
  color: "#FFFFFF",
  border: "none",
  borderRadius: "5px",
  textAlign: "center",
  cursor: "pointer",
  transition: "background-color 0.3s ease",
  textDecoration: "none",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000000",
  color: "#FFFFFF",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

type Consumo = {
  idPedido: string;
  idProduto: string;
  nomeProduto: string;
  valor: string;
};

const ListaConsumo: React.FC = () => {
  const [consumos, setConsumos] = useState<Consumo[]>([]);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const [formData, setFormData] = useState<Consumo>({
    idPedido: "",
    idProduto: "",
    nomeProduto: "",
    valor: "",
  });
  const [flashMessage, setFlashMessage] = useState<string | null>(null);

  useEffect(() => {
    const consumosArmazenados = JSON.parse(localStorage.getItem("consumos") || "[]");
    setConsumos(consumosArmazenados);
  }, []);

  const handleDelete = (index: number) => {
    const confirmDelete = window.confirm("Você tem certeza que deseja excluir este consumo?");
    if (confirmDelete) {
      const updatedConsumos = [...consumos];
      updatedConsumos.splice(index, 1);
      setConsumos(updatedConsumos);
      localStorage.setItem("consumos", JSON.stringify(updatedConsumos));
      setFlashMessage("Consumo deletado com sucesso!"); // Mensagem de sucesso
      setTimeout(() => setFlashMessage(null), 3000); // Apaga a mensagem após 3 segundos
    }
  };

  const handleEdit = (index: number) => {
    setEditIndex(index);
    setFormData({ ...consumos[index] });
  };

  const handleSave = () => {
    if (editIndex !== null) {
      const updatedConsumos = [...consumos];
      updatedConsumos[editIndex] = formData;
      setConsumos(updatedConsumos);
      localStorage.setItem("consumos", JSON.stringify(updatedConsumos));
      setEditIndex(null);
      setFlashMessage("Consumo atualizado com sucesso!"); // Mensagem de sucesso
      setTimeout(() => setFlashMessage(null), 3000); // Apaga a mensagem após 3 segundos
    }
  };

  const handleCancel = () => {
    setEditIndex(null);
    setFormData({ idPedido: "", idProduto: "", nomeProduto: "", valor: "" });
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div style={containerStyle}>
      {/* Menu lateral */}
      <nav style={navStyle}>
        <div style={logoStyle}>World Beauty</div>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      {/* Flash Message */}
      {flashMessage && (
        <div style={{
          backgroundColor: "#28a745",
          color: "#fff",
          padding: "10px",
          marginTop: "20px",
          textAlign: "center",
          borderRadius: "5px",
          position: "absolute",
          top: "20px",
          width: "100%",
          zIndex: 10
        }}>
          {flashMessage}
        </div>
      )}

      {/* Lista de Consumo */}
      <div className="container" style={{ marginLeft: "220px", width: "calc(100% - 220px)" }}>
        <h5 className="center-align">Lista de Consumo</h5>
        <table className="striped centered">
          <thead>
            <tr>
              <th>Id_Pedido</th>
              <th>Id_Produto</th>
              <th>Nome do Produto</th>
              <th>Valor</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {consumos.length > 0 ? (
              consumos.map((consumo, index) => (
                <tr key={index}>
                  <td>{consumo.idPedido}</td>
                  <td>{consumo.idProduto}</td>
                  <td>{consumo.nomeProduto}</td>
                  <td>{consumo.valor}</td>
                  <td>
                    <button onClick={() => handleEdit(index)} style={actionButtonStyle}>
                      Editar
                    </button>
                    <button onClick={() => handleDelete(index)} style={actionButtonStyle}>
                      Deletar
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan={5} style={{ textAlign: "center" }}>Nenhum consumo encontrado.</td>
              </tr>
            )}
          </tbody>
        </table>

        {/* Formulário de Edição */}
        {editIndex !== null && (
          <div style={{ marginTop: "20px" }}>
            <h5>Editar Consumo</h5>
            <div>
              <label>Id Pedido:</label>
              <input
                type="text"
                name="idPedido"
                value={formData.idPedido}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Id Produto:</label>
              <input
                type="text"
                name="idProduto"
                value={formData.idProduto}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Nome do Produto:</label>
              <input
                type="text"
                name="nomeProduto"
                value={formData.nomeProduto}
                onChange={handleChange}
              />
            </div>
            <div>
              <label>Valor:</label>
              <input
                type="text"
                name="valor"
                value={formData.valor}
                onChange={handleChange}
              />
            </div>
            <div style={{ marginTop: "10px" }}>
              <button onClick={handleSave} style={actionButtonStyle}>
                Salvar
              </button>
              <button onClick={handleCancel} style={{ ...actionButtonStyle, backgroundColor: "#e74c3c" }}>
                Cancelar
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ListaConsumo;
