// Consumo.tsx
import React from "react";

// Definindo o tipo de pedido
interface Pedido {
  id: number;
  nomeProduto: string;
  valor: number;
  nomeServico: string;
  idProduto: number;
  idServico: number;
}

// Definindo as propriedades que o componente Consumo espera receber
interface ConsumoProps {
  pedidos: Pedido[];
}

const Consumo: React.FC<ConsumoProps> = ({ pedidos }) => {
  return (
    <div>
      <h1>Lista de Consumos</h1>
      <ul>
        {pedidos.map((pedido) => (
          <li key={pedido.id}>
            <p>Produto: {pedido.nomeProduto}</p>
            <p>Serviço: {pedido.nomeServico}</p>
            <p>Valor: R${pedido.valor}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Consumo;
