import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const CadastroConsumo: React.FC = () => {
  const navigate = useNavigate();
  const [consumoSelecionado, setConsumoSelecionado] = useState({
    idPedido: "",
    idProduto: "",
    nomeProduto: "",
    valor: "",
  });

  const handleConsumoSelecionado = (idPedido: string, idProduto: string, nomeProduto: string, valor: string) => {
    setConsumoSelecionado({
      idPedido,
      idProduto,
      nomeProduto,
      valor,
    });
  };

  const handleSubmit = () => {
    navigate("/listaPedidos", { state: { novoConsumo: consumoSelecionado } });
  };

  return (
    <div className="container">
      <h5 className="center-align">Cadastro de Consumo</h5>

      <div className="row">
        <div className="col s4">
          <button
            onClick={() => handleConsumoSelecionado("1", "101", "Produto A", "50.00")}
            className="btn"
          >
            Produto A
          </button>
        </div>
        <div className="col s4">
          <button
            onClick={() => handleConsumoSelecionado("2", "102", "Produto B", "75.00")}
            className="btn"
          >
            Produto B
          </button>
        </div>
        <div className="col s4">
          <button
            onClick={() => handleConsumoSelecionado("3", "103", "Produto C", "100.00")}
            className="btn"
          >
            Produto C
          </button>
        </div>
      </div>

      <button type="button" onClick={handleSubmit} className="btn">
        Registrar
      </button>
    </div>
  );
};

export default CadastroConsumo;
