import { BrowserRouter, Navigate, Route, Routes } from "react-router-dom";
import PaginaInicial from "./paginaInicial";
import ListaCliente from "./listCliente";
import FormularioCadastroCliente from "./cadCliente";
import Produtos from "./produto";
import ListaPedidos from "./listConsumo";
import Clientes from "./cliente"; // Lista de clientes
import FormularioCadastroProduto from "./cadProduto";
import Listagens from "./listagens";

function App() {
  return (
    <BrowserRouter>
      <Routes>
        <Route index element={<PaginaInicial />} />
        <Route path="*" element={<Navigate to="/" />} />

        {/* Rotas de Clientes */}
        <Route path="/clientes" element={<Clientes />} /> {/* Exibe a lista de clientes */}
        <Route path="/formularioCadastroCliente" element={<FormularioCadastroCliente />} /> {/* Cadastro de cliente */}

        {/* Outras rotas */}
        <Route path="/produtos" element={<Produtos />} />
        <Route path="/listaPedidos" element={<ListaPedidos />} />
        <Route path="/listagens" element={<Listagens />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
