import React, { useState, useEffect } from "react";
import CSS from "csstype";

const containerStyle: CSS.Properties = {
  display: "flex",
};

const navStyle: CSS.Properties = {
  width: "250px",
  backgroundColor: "#000",
  padding: "20px",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
};

const logoStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
  marginBottom: "20px",
};

const menuLinksStyle: CSS.Properties = {
  display: "flex",
  flexDirection: "column",
  gap: "15px",
};

const botaoStyle: CSS.Properties = {
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
  padding: "10px",
  textDecoration: "none",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

type Servico = {
  nome: string;
  valor: string;
  descricao: string;
};

const ListaServicos: React.FC = () => {
  const [servicos, setServicos] = useState<Servico[]>([]);
  const [editIndex, setEditIndex] = useState<number | null>(null);
  const [novoNome, setNovoNome] = useState<string>("");
  const [novoValor, setNovoValor] = useState<string>("");
  const [novaDescricao, setNovaDescricao] = useState<string>("");

  useEffect(() => {
    const servicosArmazenados = JSON.parse(localStorage.getItem("servicos") || "[]");
    setServicos(servicosArmazenados);
  }, []);

  const handleDelete = (index: number) => {
    const updatedServicos = servicos.filter((_, i) => i !== index);
    setServicos(updatedServicos);
    localStorage.setItem("servicos", JSON.stringify(updatedServicos));
    alert("Serviço deletado com sucesso!");
  };

  const handleEdit = (index: number) => {
    const servico = servicos[index];
    setEditIndex(index);
    setNovoNome(servico.nome);
    setNovoValor(servico.valor);
    setNovaDescricao(servico.descricao);
  };

  const handleSave = () => {
    if (editIndex !== null) {
      const updatedServicos = [...servicos];
      updatedServicos[editIndex] = { nome: novoNome, valor: novoValor, descricao: novaDescricao };
      setServicos(updatedServicos);
      localStorage.setItem("servicos", JSON.stringify(updatedServicos));
      setEditIndex(null);
      resetForm();
    }
  };

  const handleCancel = () => {
    setEditIndex(null);
    resetForm();
  };

  const resetForm = () => {
    setNovoNome("");
    setNovoValor("");
    setNovaDescricao("");
  };

  return (
    <div style={containerStyle}>
      <nav style={navStyle}>
        <a style={logoStyle}>World Beauty</a>
        <div style={menuLinksStyle}>
          <a style={botaoStyle} href="/cadServico">Cadastros</a>
          <a style={botaoStyle} href="/listaCliente">Cliente</a>
          <a style={botaoStyle} href="/listaProduto">Produto</a>
          <a style={botaoStyle} href="/listaPedidos">Consumo</a>
          <a style={botaoStyle} href="/listaServicos">Serviços</a>
          <a style={botaoStyle} href="/Listagens">Listagens</a>
        </div>
      </nav>

      <div style={{ marginLeft: "270px", width: "calc(100% - 270px)" }}>
        <h5 className="center-align">Lista de Serviços</h5>

        {editIndex !== null && (
          <div className="row">
            <div className="col s12">
              <h6>Editar Serviço</h6>
              <div className="input-field">
                <input
                  type="text"
                  value={novoNome}
                  onChange={(e) => setNovoNome(e.target.value)}
                />
              </div>
              <div className="input-field">
                <input
                  type="text"
                  value={novoValor}
                  onChange={(e) => setNovoValor(e.target.value)}
                />
              </div>
              <div className="input-field">
                <input
                  type="text"
                  value={novaDescricao}
                  onChange={(e) => setNovaDescricao(e.target.value)}
                />
              </div>
              <div>
                <button onClick={handleSave} style={actionButtonStyle}>Salvar</button>
                <button onClick={handleCancel} style={{ ...actionButtonStyle, backgroundColor: "#e74c3c" }}>Cancelar</button>
              </div>
            </div>
          </div>
        )}

        <table className="striped centered">
          <thead>
            <tr>
              <th>Serviço</th>
              <th>Preço</th>
              <th>Descrição</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {servicos.length === 0 ? (
              <tr>
                <td colSpan={4} className="center-align">
                  Nenhum serviço cadastrado
                </td>
              </tr>
            ) : (
              servicos.map((servico, index) => (
                <tr key={index}>
                  <td>{servico.nome}</td>
                  <td>{servico.valor}</td>
                  <td>{servico.descricao}</td>
                  <td>
                    <button onClick={() => handleEdit(index)} style={actionButtonStyle}>Editar</button>
                    <button onClick={() => handleDelete(index)} style={actionButtonStyle}>Deletar</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListaServicos;
