import React, { Component } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

// Definir o tipo Cliente
type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
  genero: string;
};

// Definir o tipo de State
type State = {
  clientes: Cliente[];
  isEditing: boolean;
  editIndex: number;
  editNome: string;
  editCpf: string;
  editRg: string;
  editDataEmissao: string;
  editTelefone: string;
  editDataCadastro: string;
  editGenero: string[]; // Alterado para ser um array de strings (para permitir múltiplos gêneros)
};

// Estilos em CSS inline
const backgroundColor: CSS.Properties = {
  backgroundColor: "#000",
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff",
};

const actionButtonStyle: CSS.Properties = {
  backgroundColor: "#000",
  color: "#fff",
  padding: "8px 16px",
  border: "none",
  cursor: "pointer",
  marginLeft: "10px",
};

class ListaClientes extends Component<{}, State> {
  constructor(props: {}) {
    super(props);
    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    this.state = {
      clientes,
      isEditing: false,
      editIndex: -1,
      editNome: "",
      editCpf: "",
      editRg: "",
      editDataEmissao: "",
      editTelefone: "",
      editDataCadastro: "",
      editGenero: [], // Inicializa como array vazio
    };
  }

  handleEdit = (index: number) => {
    const cliente = this.state.clientes[index];
    this.setState({
      isEditing: true,
      editIndex: index,
      editNome: cliente.nome,
      editCpf: cliente.cpf,
      editRg: cliente.rg,
      editDataEmissao: cliente.dataEmissao,
      editTelefone: cliente.telefone,
      editDataCadastro: cliente.dataCadastro,
      editGenero: cliente.genero ? [cliente.genero] : [], // Converte gênero em array
    });
  };

  handleSaveEdit = () => {
    const { clientes, editIndex, editNome, editCpf, editRg, editDataEmissao, editTelefone, editDataCadastro, editGenero } = this.state;
    const updatedClientes = [...clientes];
    updatedClientes[editIndex] = {
      ...updatedClientes[editIndex],
      nome: editNome,
      cpf: editCpf,
      rg: editRg,
      dataEmissao: editDataEmissao,
      telefone: editTelefone,
      dataCadastro: editDataCadastro,
      genero: editGenero.join(", "), // Converte array de gênero para string
    };

    localStorage.setItem("clientes", JSON.stringify(updatedClientes));
    this.setState({
      clientes: updatedClientes,
      isEditing: false,
      editIndex: -1,
      editNome: "",
      editCpf: "",
      editRg: "",
      editDataEmissao: "",
      editTelefone: "",
      editDataCadastro: "",
      editGenero: [],
    });
  };

  handleDelete = (index: number) => {
    const clientes = this.state.clientes;
    clientes.splice(index, 1);
    localStorage.setItem("clientes", JSON.stringify(clientes));
    this.setState({ clientes });
    alert("Cliente deletado com sucesso!");
  };

  handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value, type, checked } = e.target;
    if (type === "checkbox") {
      const { editGenero } = this.state;
      this.setState({
        editGenero: checked
          ? [...editGenero, value]
          : editGenero.filter((g) => g !== value),
      });
    } else {
      this.setState({ [name]: value } as any);
    }
  };

  render() {
    const { clientes, isEditing, editNome, editCpf, editRg, editDataEmissao, editTelefone, editDataCadastro, editGenero } = this.state;

    return (
      <div>
        <nav>
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>World Beauty</a>
            <a style={botaoStyle} href="/cadServico">Cadastros</a>
            <a style={botaoStyle} href="/listaCliente">Cliente</a>
            <a style={botaoStyle} href="/listaProduto">Produto</a>
            <a style={botaoStyle} href="/listaPedidos">Consumo</a>
            <a style={botaoStyle} href="/listaServicos">Serviços</a>
            <a style={botaoStyle} href="/Listagens">Listagens</a>
          </div>
        </nav>
        <br />
        <div className="container">
          <h5 className="center-align">Lista de Clientes</h5>

          {isEditing && (
            <div>
              <h6>Editar Cliente</h6>
              <input type="text" name="editNome" value={editNome} onChange={this.handleInputChange} placeholder="Nome do Cliente" />
              <input type="text" name="editCpf" value={editCpf} onChange={this.handleInputChange} placeholder="CPF" />
              <input type="text" name="editRg" value={editRg} onChange={this.handleInputChange} placeholder="RG" />
              <input type="text" name="editDataEmissao" value={editDataEmissao} onChange={this.handleInputChange} placeholder="Data da Emissão" />
              <input type="text" name="editTelefone" value={editTelefone} onChange={this.handleInputChange} placeholder="Telefone" />
              <input type="text" name="editDataCadastro" value={editDataCadastro} onChange={this.handleInputChange} placeholder="Data de Cadastro" />

              <h6>Gênero</h6>
              <label>
                <input type="checkbox" value="Masculino" checked={editGenero.includes("Masculino")} onChange={this.handleInputChange} />
                <span>Masculino</span>
              </label>
              <label>
                <input type="checkbox" value="Feminino" checked={editGenero.includes("Feminino")} onChange={this.handleInputChange} />
                <span>Feminino</span>
              </label>
              <label>
                <input type="checkbox" value="Outro" checked={editGenero.includes("Outro")} onChange={this.handleInputChange} />
                <span>Outro</span>
              </label>

              <button onClick={this.handleSaveEdit} style={actionButtonStyle}>Salvar</button>
              <button onClick={() => this.setState({ isEditing: false })} style={actionButtonStyle}>Cancelar</button>
            </div>
          )}

          <table className="striped centered">
            <thead>
              <tr>
                <th>Nome</th>
                <th>CPF</th>
                <th>RG</th>
                <th>Data Emissão</th>
                <th>Telefone</th>
                <th>Data Cadastro</th>
                <th>Gênero</th>
                <th>Ações</th>
              </tr>
            </thead>
            <tbody>
              {clientes.length > 0 ? (
                clientes.map((cliente: Cliente, index: number) => (
                  <tr key={index}>
                    <td>{cliente.nome}</td>
                    <td>{cliente.cpf}</td>
                    <td>{cliente.rg}</td>
                    <td>{cliente.dataEmissao}</td>
                    <td>{cliente.telefone}</td>
                    <td>{cliente.dataCadastro}</td>
                    <td>{cliente.genero}</td>
                    <td style={{ textAlign: "right" }}>
                      <button onClick={() => this.handleEdit(index)} style={actionButtonStyle}>Editar</button>
                      <button onClick={() => this.handleDelete(index)} style={actionButtonStyle}>Deletar</button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan={8}>Nenhum cliente cadastrado</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListaClientes;
