import { Component } from "react";
import CSS from "csstype";
import "materialize-css/dist/css/materialize.min.css";

type Props = {
  tema: string;
};

const backgroundColor: CSS.Properties = {
  backgroundColor: "#000", // Fundo preto
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000", // Fundo do botão preto
  color: "#fff", // Texto do botão branco
  border: "none", // Remove bordas
  cursor: "pointer", // Alterar cursor para indicar que é clicável
};

const fontStyle: CSS.Properties = {
  fontSize: "xx-large",
  fontFamily: "arial",
  color: "#fff", // Texto branco
};

class ListaConsumos extends Component<any, Props> {
  state = {
    cliente: "",
    servico: "",
    produto: "",
  };

  componentDidMount() {
    // Obtém os parâmetros da URL
    const params = new URLSearchParams(window.location.search);
    const cliente = params.get("cliente");
    const servico = params.get("servico");
    const produto = params.get("produto");

    this.setState({
      cliente: cliente || "",
      servico: servico || "",
      produto: produto || "",
    });
  }

  render() {
    const { cliente, servico, produto } = this.state;
    return (
      <div>
        <nav className="">
          <div style={backgroundColor} className="nav-wrapper">
            <a className="brand-logo right" style={fontStyle}>
              World Beauty
            </a>
            {/* Links de navegação */}
          </div>
        </nav>

        <br />
        <div className="container">
          <h5 className="center-align">Lista de Consumo</h5>

          <table className="striped centered">
            <thead>
              <tr>
                <th>Cliente</th>
                <th>Serviço</th>
                <th>Produto</th>
                <th>Preço</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{cliente}</td>
                <td>{servico}</td>
                <td>{produto}</td>
                <td>R$ 30,00</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default ListaConsumos;
