import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import ListaPedidos from "./listConsumo"; // Certifique-se de que o caminho está correto
import ListaServicos from "./listServico";
import FormularioCadastroCliente from "./cadCliente";
import FormularioCadastroProduto from "./cadProduto";
import FormularioCadastroServico from "./cadServico";
import FormularioCadastroConsumo from "./cadConsumo";

const App: React.FC = () => {
  const [pedidos, setPedidos] = useState<any[]>([]); // Aqui você gerencia o estado dos pedidos

  const handleEditar = (pedidoId: number) => {
    console.log(`Editar pedido com ID: ${pedidoId}`);
    // Implemente a lógica de edição
  };

  const handleExcluir = (pedidoId: number) => {
    console.log(`Excluir pedido com ID: ${pedidoId}`);
    // Implemente a lógica de exclusão
  };

  const handleCadastroConsumo = (cliente: string, servico: string, produto: string, preco: number) => {
    // Adicione um novo pedido ao estado
    setPedidos([
      ...pedidos,
      {
        id: pedidos.length + 1, // Gerando um ID simples para o exemplo
        nomeProduto: produto,
        valor: preco,
        nomeServico: servico,
        idProduto: Number(produto),
        idServico: Number(servico),
      },
    ]);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/listaPedidos"
          element={<ListaPedidos pedidos={pedidos} onEditar={handleEditar} onExcluir={handleExcluir} />}
        />
        <Route path="/listaServicos" element={<ListaServicos />} />
        <Route path="/formularioCadastroCliente" element={<FormularioCadastroCliente />} />
        <Route path="/formularioCadastroProduto" element={<FormularioCadastroProduto />} />
        <Route path="/formularioCadastroServico" element={<FormularioCadastroServico />} />
        <Route
          path="/formularioCadastroPedido"
          element={<FormularioCadastroConsumo onCadastroSubmit={handleCadastroConsumo} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
