import { Component } from "react";
import CSS from "csstype";
import editar from "../Assets/edit.png";
import excluir from "../Assets/delete.png";

type Cliente = {
  id: string;
  nome: string;
  cpf: string;
  rg: string;
  dataEmissao: string;
  telefone: string;
  dataCadastro: string;
};

const botaoStyle: CSS.Properties = {
  padding: "10px",
  fontFamily: "arial",
  backgroundColor: "#000",
  color: "#fff",
  border: "none",
  cursor: "pointer",
};

export default class ListaClientes extends Component {
  state = {
    clientes: [] as Cliente[],
  };

  componentDidMount() {
    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    this.setState({ clientes });
  }

  handleExcluir = (id: string) => {
    const clientes = JSON.parse(localStorage.getItem("clientes") || "[]");
    const clientesAtualizados = clientes.filter((cliente: Cliente) => cliente.id !== id);
    localStorage.setItem("clientes", JSON.stringify(clientesAtualizados));
    this.setState({ clientes: clientesAtualizados });
    alert("Cliente excluído com sucesso!");
  };

  render() {
    const { clientes } = this.state;

    return (
      <div className="container">
        <h5>Lista de Clientes</h5>
        <table className="highlight centered">
          <thead>
            <tr>
              <th>Id_Cliente</th>
              <th>Nome</th>
              <th>CPF</th>
              <th>RG</th>
              <th>Data Emissão</th>
              <th>Telefone</th>
              <th>Data Cadastro</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {clientes.map((cliente) => (
              <tr key={cliente.id}>
                <td>{cliente.id}</td>
                <td>{cliente.nome}</td>
                <td>{cliente.cpf}</td>
                <td>{cliente.rg}</td>
                <td>{cliente.dataEmissao}</td>
                <td>{cliente.telefone}</td>
                <td>{cliente.dataCadastro}</td>
                <td>
                  <img
                    src={editar}
                    alt="Editar"
                    style={{ cursor: "pointer", marginRight: "10px" }}
                  />
                  <img
                    src={excluir}
                    alt="Excluir"
                    style={{ cursor: "pointer" }}
                    onClick={() => this.handleExcluir(cliente.id)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}
